            });

            // Í≤åÏûÑ Ï¢ÖÎ£å Ï≤òÎ¶¨Î¶¨
            if (gameOver || gameClear) {
                const centerX = canvas.width / 2;
                const centerY = canvas.height / 2;
                ctx.font = "24px Arial";
                ctx.textAlign = "center";
                ctx.fillStyle = "white";
                ctx.fillText(gameOver ? "üò• GAME OVER" : "üéâ MISSION COMPLETE!", centerX, centerY - 20);
                ctx.font = "18px Arial";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 10);
                ctx.fillText("üîÑ", centerX, centerY + 40);
                return; // draw Ï§ëÎã®
            }

            // Î∏åÎ¶≠Í±∏ Ïä§ÌîÑÎùºÏù¥Ìä∏ Í∑∏Î¶¨Í∏∞
            // ctx.font = "20px Arial";
            // ctx.fillText("üß±", girlX, girlYRef.current);
            ctx.drawImage(
                spriteRef.current,
                frameX.current * frameWidth,
                frameY.current * frameHeight,
                frameWidth,
                frameHeight,
                girlX,
                girlYRef.current,
                frameWidth * scale,
                frameHeight * scale
            );

            // ÌîÑÎ†àÏûÑ Ï†ÑÌôò Ïï†ÎãàÎ©îÏù¥ÏÖò
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
                if (frameX.current === 0) frameY.current = (frameY.current + 1) % numRows;
            }
            frameCount.current++;

            // Î∏åÎ¶≠Í±∏ Ïù¥Îèô
            if (keys.current.up && girlYRef.current > -5) girlYRef.current -= girlSpeed;    // 20 Ïà´ÏûêÎ•º Ï§ÑÏù¥Î©¥ ÏúÑÎ°ú Îçî Ïù¥Îèô Í∞ÄÎä•(10)
            if (keys.current.down && girlYRef.current < canvas.height - 50) girlYRef.current += girlSpeed;  // -20 Ïà´ÏûêÎ•º Ïò¨Î¶¨Î©¥ ÏúÑÎ°ú Ïù¥Îèô(-50)

            // Î≤ΩÎèå Í∑∏Î¶¨Í∏∞
            ctx.font = "16px Arial";
            bricksRef.current.forEach((b, i) => {
                b.x += b.speedX;
                b.y += b.speedY;
                ctx.fillText("üß±", b.x, b.y);

                // Ï†ÅÍ≥º Ï∂©Îèå ÌôïÏù∏
                enemiesRef.current.forEach((e, j) => {
                    const dx = b.x - e.x, dy = b.y - e.y, dist = Math.sqrt(dx * dx + dy * dy);
                    if (dist < 20) {
                        enemiesRef.current.splice(j, 1);
                        bricksRef.current.splice(i, 1);
                        scoreRef.current++;
                        setScore(scoreRef.current);
                        if (scoreRef.current >= 50) setGameClear(true);
                    }
                });
            });

            // Ï†Å Ïù¥Îèô Î∞è Ï∂©Îèå
            ctx.fillStyle = "#f472b6";
            ctx.font = "20px Arial";
            enemiesRef.current.forEach((e, i) => {
                e.x -= e.speed;
                ctx.fillText(e.emoji, e.x, e.y);
                if (e.x < girlX + 10 && e.x > girlX - 10 && e.y > girlYRef.current - 10 && e.y < girlYRef.current + 10)
                    setGameOver(true);
                if (e.x < -30) {
                    enemiesRef.current.splice(i, 1);
                    scoreRef.current = Math.max(0, scoreRef.current - 1);
                    setScore(scoreRef.current);
                }
            });

            // ÏïÑÏù¥ÌÖú Îì±Ïû•
            if (itemRef.current) {
                // ÏïÑÏù¥ÌÖú Í∑∏Î¶¨Í∏∞
                ctx.font = "20px Arial";
                ctx.fillText("‚≠ê", itemRef.current.x, itemRef.current.y);

                // ÏïÑÏù¥ÌÖúÍ≥º Î∏åÎ¶≠Í±∏ Ï∂©Îèå Ï≤¥ÌÅ¨
                itemRef.current.x -= itemRef.current.speed;
                const girlRight = girlX + frameWidth * scale;
                const girlBottom = girlYRef.current + frameHeight * scale;
                if (
                    itemRef.current.x < girlRight &&            // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏôºÏ™ΩÏúºÎ°ú Îì§Ïñ¥Ïò§Í≥†
                    itemRef.current.y > girlYRef.current &&     // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏïÑÎûòÏ™ΩÏù¥Î©∞
                    itemRef.current.y < girlBottom              // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏúÑÏ™Ω
                ) {
                    // Ìä∏Î¶¨ÌîåÏÉ∑ Î∞úÎèô
                    setTripleShot(true);
                    clearTimeout(tripleShotTimerRef.current);
                    tripleShotTimerRef.current = setTimeout(() => setTripleShot(false), 5000);
                    // ÏïÑÏù¥ÌÖú Ï†úÍ±∞
                    itemRef.current = null;
                }
            }
            // if (itemRef.current) {
            //     itemRef.current.x -= itemRef.current.speed;

            //     // ÏïÑÏù¥ÌÖúÍ≥º Î∏åÎ¶≠Í±∏ Ï∂©Îèå Ï≤¥ÌÅ¨
            //     const item = itemRef.current;
            //     const girlTop = girlYRef.current;
            //     const girlBottom = girlYRef.current + frameHeight * 0.1; // scaleÍ≥º ÏùºÏπò
            //     const girlRight = girlX + frameWidth * 0.1;

            //     if (
            //         item.x < girlRight &&         // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏôºÏ™ΩÏúºÎ°ú Îì§Ïñ¥Ïò§Í≥†
            //         item.y > girlTop &&           // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏïÑÎûòÏ™ΩÏù¥Î©∞
            //         item.y < girlBottom           // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏúÑÏ™Ω
            //     ) {
            //         // Ìä∏Î¶¨ÌîåÏÉ∑ Î∞úÎèô
            //         setTripleShot(true);
            //         clearTimeout(tripleShotTimerRef.current);
            //         tripleShotTimerRef.current = setTimeout(() => setTripleShot(false), 5000);

            //         // ÏïÑÏù¥ÌÖú Ï†úÍ±∞
            //         itemRef.current = null;
            //     }

            //     // ÌôîÎ©¥ Î∞ñÏúºÎ°ú ÎÇòÍ∞ÄÎ©¥ Ï†úÍ±∞
            //     if (item.x < -20) {
            //         itemRef.current = null;
            //     }
            // }

            // Ï†êÏàò & Ìä∏Î¶¨ÌîåÏÉ∑ ÌëúÏãú
            ctx.font = "16px Arial";
            ctx.fillStyle = "#fbbf24";
            ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);

            if (tripleShot) {
                ctx.fillStyle = "#60a5fa";
                ctx.fillText("üî• Triple Shot!", 90, 30);
            }
            // Î∞òÎ≥µ Ìò∏Ï∂ú
            animationRef.current = requestAnimationFrame(draw);
        };

        animationRef.current = requestAnimationFrame(draw);

        // draw();

        const handleTouchStart = (e) => {
            isTouching.current = true;
            const touchY = e.touches[0].clientY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 0), canvas.height - frameHeight * scale);

            if (!shootIntervalRef.current) {
                fireBricksRef.current();
                shootIntervalRef.current = setInterval(() => fireBricksRef.current(), 250);
            }
        };

        const handleTouchMove = (e) => {
            const touchY = e.touches[0].clientY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 0), canvas.height - frameHeight * scale);
        };

        const handleTouchEnd = () => {
            isTouching.current = false;
            clearInterval(shootIntervalRef.current);
            shootIntervalRef.current = null;
        };

        // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏
        window.addEventListener("keydown", (e) => {
            if (e.key === "ArrowUp") keys.current.up = true;
            if (e.key === "ArrowDown") keys.current.down = true;
            if (e.key === " " && !shootCooldownRef.current && !gameOver) fireBricksRef.current();
            if ((e.key === "r" || e.key === "R") && (gameOver || gameClear)) window.location.reload();
        });
        window.addEventListener("keyup", (e) => {
            if (e.key === "ArrowUp") keys.current.up = false;
            if (e.key === "ArrowDown") keys.current.down = false;
        });

        // Ïù¥Î≤§Ìä∏ Î∞îÏù∏Îî©
        canvas.addEventListener("touchstart", handleTouchStart, { passive: false });
        canvas.addEventListener("touchmove", handleTouchMove, { passive: false });
        canvas.addEventListener("touchend", handleTouchEnd, { passive: false });

        return () => {
            cancelAnimationFrame(animationRef.current);
            clearInterval(shootIntervalRef.current);
        };
    }, [spriteLoaded, canvasSize, gameOver, gameClear, tripleShot]);


    // Ï†Å Îì±Ïû• Í∞ÑÍ≤©
    useEffect(() => {
        const interval = setInterval(() => {
            if (!gameOver && !gameClear) {
                const emojis = ["üöÄ", "üëΩ", "üõ∏"];
                enemiesRef.current.push({
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 20) + 10,
                    speed: Math.random() * 1.5 + 1.5,
                    emoji: emojis[Math.floor(Math.random() * emojis.length)],
                });
            }
        }, 1500);
        return () => clearInterval(interval);
    }, [canvasSize, gameOver, gameClear]);

    // ÏïÑÏù¥ÌÖú ÏÉùÏÑ±
    useEffect(() => {
        const interval = setInterval(() => {
            if (!gameOver && !gameClear && !itemRef.current) {
                itemRef.current = {
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 30) + 10,
                    speed: 2,
                };
            }
        }, 10000);
        return () => clearInterval(interval);
    }, [canvasSize, gameOver, gameClear]);

    // Î∞òÏùëÌòï Ï∫îÎ≤ÑÏä§
    useEffect(() => {
        const updateSize = () => {
            const width = Math.min(window.innerWidth - 10, 480);
            const height = Math.round(width * 0.75);
            setCanvasSize({ width, height });
        };
        updateSize();
        window.addEventListener("resize", updateSize);
        return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
        <div className="flex flex-col items-center mt-6">
            <h2 className="text-2xl font-bold mb-4">üß± Brick Girl: Space Defense</h2>
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                className="bg-black rounded shadow-md touch-none"
                style={{ width: "100%", maxWidth: "480px" }}
            />
        </div>
    );
}
