import { useLanguage } from "../context/LanguageContext";
import { useEffect, useState } from "react";
import ParticlesBackground from "../components/ParticlesBackground";

export default function Home() {
  const { language } = useLanguage();
  const [show, setShow] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setShow(true), 300);
    return () => clearTimeout(timer);
  }, []);

  return (
    <main className="relative min-h-screen flex items-center justify-center bg-white text-gray-800 dark:bg-gray-900 dark:text-gray-100 overflow-hidden">
      <ParticlesBackground />
      <div className={`text-center p-6 z-10 ${show ? "fade-in-up" : ""}`}>
        <h1 className="text-5xl font-bold mb-4">Youngjee Park</h1>
        <p className="text-xl">
          {language === "en"
            ? "Passionate Developer & Creative Thinker"
            : "열정적인 개발자, 창의적인 사고를 가진 사람"}
        </p>
      </div>

      <style>
        {`
          .fade-in-up {
            opacity: 0;
            transform: translateY(20px);
            animation: fadeInUp 1s ease forwards;
          }

          @keyframes fadeInUp {
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
        `}
      </style>
    </main>
  );
}
