import React, { useRef, useEffect, useState } from 'react';

const BrickGirlRT = () => {
    const canvasRef = useRef(null);
    const spriteRef = useRef(new Image());

    // 스프라이트 설정
    const frameWidth = 335;
    const frameHeight = 512;
    const numCols = 3;
    const scale = 0.2;

    const frameX = useRef(0);
    const frameCount = useRef(0);
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // 브릭걸 위치 및 점프 관련 변수
    let girlX = 50;
    let girlY = 0;
    let velocityY = 0;
    let jumpCount = 0;
    const maxJumps = 2;
    const gravity = 0.6;

    const bricks = useRef([]); // 벽돌 배열

    const scrollSpeed = 2;
    const tileSize = 50;
    const groundTiles = useRef([]);

    const levelData = [
        { x: 0, y: 350 }, { x: 50, y: 350 }, { x: 100, y: 350 },
        { x: 150, y: 350 }, { x: 200, y: 350 },
        // 간격 (점프 유도)
        { x: 250, y: 350 }, { x: 350, y: 350 },
        { x: 400, y: 350 },
        // 높은 지형
        { x: 500, y: 300 }, { x: 550, y: 300 },
        { x: 600, y: 250 }, { x: 650, y: 250 },
        { x: 700, y: 200 }, { x: 750, y: 200 },
    ];

    // 초기 타일 로딩
    useEffect(() => {
        groundTiles.current = levelData.map(tile => ({ ...tile, type: 'ground' }));
    }, []);

    // 착지 여부 체크 함수
    const isOnGround = () => {
        const feetX = girlX + (frameWidth * scale) / 2;
        const feetY = girlY + frameHeight * scale;

        return groundTiles.current.some(tile =>
            feetX > tile.x &&
            feetX < tile.x + tileSize &&
            feetY >= tile.y &&
            feetY <= tile.y + 10
        );
    };

    // 벽돌 던지기 함수
    const throwBrick = () => {
        const startX = girlX + 30;
        const startY = girlY + 10;
        const vx = 6 + Math.random();
        const vy = -8;
        bricks.current.push({ x: startX, y: startY, vx, vy });
    };

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let animationFrameId;

        canvas.width = 800;
        canvas.height = 400;

        // 스프라이트 이미지 로딩
        spriteRef.current.src = process.env.PUBLIC_URL + '/brickgirl_sprite.png';
        spriteRef.current.onload = () => {
            setSpriteLoaded(true);
            const standingTile = groundTiles.current.find(tile =>
                girlX + (frameWidth * scale) / 2 > tile.x &&
                girlX + (frameWidth * scale) / 2 < tile.x + tileSize
            );
            if (standingTile) {
                girlY = standingTile.y - frameHeight * scale;
            }
        };

        // 키 입력 처리
        const handleKeyDown = (e) => {
            if (e.code === 'Space') {
                e.preventDefault();
                throwBrick();
            } else if (e.code === 'ArrowUp') {
                if (jumpCount < maxJumps) {
                    velocityY = -12 * (jumpCount === 1 ? 1.2 : 1);
                    jumpCount++;
                }
            }
        };

        // 모바일 터치 조작 처리
        const handleTouchStart = (e) => {
            const touchX = e.touches[0].clientX;
            if (touchX < canvas.width / 2) {
                if (jumpCount < maxJumps) {
                    velocityY = -12;
                    jumpCount++;
                }
            } else {
                throwBrick();
            }
        };

        const updateFrame = () => {
            frameCount.current++;
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
            }
        };

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // 지형 스크롤 및 그리기
            groundTiles.current.forEach(tile => {
                tile.x -= scrollSpeed;
                ctx.fillStyle = 'saddlebrown';
                ctx.fillRect(tile.x, tile.y, tileSize, tileSize);
            });

            // 무한 지형
            const lastTile = groundTiles.current[groundTiles.current.length - 1];
            if (lastTile && lastTile.x < canvas.width) {
                groundTiles.current.push({
                    x: lastTile.x + tileSize,
                    y: 350, // 임시 기본 높이
                    type: 'ground',
                });
            }

            // 중력 적용 및 점프 처리
            velocityY += gravity;
            girlY += velocityY;

            // 착지 처리
            if (isOnGround()) {
                velocityY = 0;
                jumpCount = 0;
                const landingTile = groundTiles.current.find(tile =>
                    girlX + (frameWidth * scale) / 2 > tile.x &&
                    girlX + (frameWidth * scale) / 2 < tile.x + tileSize
                );
                if (landingTile) {
                    girlY = landingTile.y - frameHeight * scale;
                }
            }

            // 벽돌 처리
            ctx.font = '16px Arial';
            bricks.current.forEach((brick, index) => {
                brick.x += brick.vx;
                brick.vy += gravity * 0.5;
                brick.y += brick.vy;
                ctx.fillText('🧱', brick.x, brick.y);
                if (brick.x > canvas.width || brick.y > canvas.height) {
                    bricks.current.splice(index, 1);
                }
            });

            // 스프라이트 그리기
            if (spriteLoaded) {
                updateFrame();
                ctx.drawImage(
                    spriteRef.current,
                    frameX.current * frameWidth,
                    0,
                    frameWidth,
                    frameHeight,
                    girlX,
                    girlY,
                    frameWidth * scale,
                    frameHeight * scale
                );
            } else {
                ctx.fillStyle = 'hotpink';
                ctx.fillRect(girlX, girlY, 30, 30);
            }

            animationFrameId = requestAnimationFrame(draw);
        };

        // 이벤트 리스너 및 루프 시작
        window.addEventListener('keydown', handleKeyDown);
        canvas.addEventListener('touchstart', handleTouchStart);
        draw();

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            canvas.removeEventListener('touchstart', handleTouchStart);
            cancelAnimationFrame(animationFrameId);
        };
    }, [spriteLoaded]);

    return (
        <div style={{ textAlign: 'center' }}>
            <h2>Brick Girl: Run & Throw</h2>
            <canvas ref={canvasRef} style={{ border: '2px solid #999' }} />
            <p>⬆️: Jump / Space: 🧱 (모바일은 화면 좌우 터치)</p>
        </div>
    );
};

export default BrickGirlRT;
