import { useEffect, useRef, useState } from "react";

export default function BrickBreaker() {
  const canvasRef = useRef(null);

  // 게임 상태
  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(
    parseInt(localStorage.getItem("highScore")) || 0
  );
  const [lives, setLives] = useState(3);
  const [gameOver, setGameOver] = useState(false);
  const [gameClear, setGameClear] = useState(false);

  // 설정값
  const canvasWidth = 400;
  const canvasHeight = 300;
  const ballRadius = 8;
  const paddleHeight = 10;
  const paddleWidth = 75;
  const brickRowCount = 3;
  const brickColumnCount = 6;
  const brickWidth = 50;
  const brickHeight = 20;
  const brickPadding = 10;
  const brickOffsetTop = 30;
  const brickOffsetLeft = 25;

  // 움직이는 객체 상태
  const ball = useRef({ x: 200, y: 200, dx: 2, dy: -2 });
  const paddleX = useRef((canvasWidth - paddleWidth) / 2);
  const keys = useRef({ left: false, right: false });
  const bricks = useRef([]);
  const isAnimating = useRef(false);

  // 초기 벽돌 세팅
  useEffect(() => {
    const temp = [];
    for (let c = 0; c < brickColumnCount; c++) {
      temp[c] = [];
      for (let r = 0; r < brickRowCount; r++) {
        temp[c][r] = { x: 0, y: 0, status: 1 };
      }
    }
    bricks.current = temp;
  }, []);

  const keyDownHandler = (e) => {
    if (e.key === "Right" || e.key === "ArrowRight") keys.current.right = true;
    else if (e.key === "Left" || e.key === "ArrowLeft") keys.current.left = true;
  };

  const keyUpHandler = (e) => {
    if (e.key === "Right" || e.key === "ArrowRight") keys.current.right = false;
    else if (e.key === "Left" || e.key === "ArrowLeft") keys.current.left = false;
  };

  const draw = (ctx) => {
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Draw bricks
    let remainingBricks = 0;
    for (let c = 0; c < brickColumnCount; c++) {
      for (let r = 0; r < brickRowCount; r++) {
        const b = bricks.current[c][r];
        if (b.status === 1) {
          const brickX = c * (brickWidth + brickPadding) + brickOffsetLeft;
          const brickY = r * (brickHeight + brickPadding) + brickOffsetTop;
          b.x = brickX;
          b.y = brickY;
          ctx.beginPath();
          ctx.rect(brickX, brickY, brickWidth, brickHeight);
          ctx.fillStyle = "#fbbf24";
          ctx.fill();
          ctx.closePath();
          remainingBricks++;
        }
      }
    }

    if (remainingBricks === 0 && !gameClear) {
      setGameClear(true);
      isAnimating.current = false;
      return;
    }

    // Draw ball
    ctx.beginPath();
    ctx.arc(ball.current.x, ball.current.y, ballRadius, 0, Math.PI * 2);
    ctx.fillStyle = "#ff6347";
    ctx.fill();
    ctx.closePath();

    // Draw paddle
    ctx.beginPath();
    ctx.rect(paddleX.current, canvasHeight - paddleHeight, paddleWidth, paddleHeight);
    ctx.fillStyle = "#0095DD";
    ctx.fill();
    ctx.closePath();

    // Draw score
    ctx.font = "14px Arial";
    ctx.fillStyle = "#ffffff";
    ctx.fillText(`Score: ${score}`, 8, 20);
    ctx.fillText(`High: ${highScore}`, 8, 40);
    ctx.fillText(`Lives: ${lives}`, canvasWidth - 80, 20);

    // Ball collision with bricks
    for (let c = 0; c < brickColumnCount; c++) {
      for (let r = 0; r < brickRowCount; r++) {
        const b = bricks.current[c][r];
        if (b.status === 1) {
          if (
            ball.current.x > b.x &&
            ball.current.x < b.x + brickWidth &&
            ball.current.y > b.y &&
            ball.current.y < b.y + brickHeight
          ) {
            ball.current.dy = -ball.current.dy;
            b.status = 0;
            const newScore = score + 1;
            setScore(newScore);
            if (newScore > highScore) {
              setHighScore(newScore);
              localStorage.setItem("highScore", newScore);
            }
          }
        }
      }
    }

    // Ball collision with wall
    if (
      ball.current.x + ball.current.dx > canvasWidth - ballRadius ||
      ball.current.x + ball.current.dx < ballRadius
    )
      ball.current.dx = -ball.current.dx;
    if (ball.current.y + ball.current.dy < ballRadius) ball.current.dy = -ball.current.dy;
    else if (ball.current.y + ball.current.dy > canvasHeight - ballRadius) {
      if (
        ball.current.x > paddleX.current &&
        ball.current.x < paddleX.current + paddleWidth
      ) {
        ball.current.dy = -ball.current.dy;
      } else {
        const newLives = lives - 1;
        if (newLives <= 0) {
          setGameOver(true);
          isAnimating.current = false;
          return;
        } else {
          setLives(newLives);
          ball.current = { x: 200, y: 200, dx: 2, dy: -2 };
          paddleX.current = (canvasWidth - paddleWidth) / 2;
        }
      }
    }

    // Move paddle
    if (keys.current.right && paddleX.current < canvasWidth - paddleWidth)
      paddleX.current += 5;
    else if (keys.current.left && paddleX.current > 0)
      paddleX.current -= 5;

    // Move ball
    ball.current.x += ball.current.dx;
    ball.current.y += ball.current.dy;

    if (isAnimating.current) requestAnimationFrame(() => draw(ctx));
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    document.addEventListener("keydown", keyDownHandler);
    document.addEventListener("keyup", keyUpHandler);

    isAnimating.current = true;
    draw(ctx);

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
      document.removeEventListener("keyup", keyUpHandler);
      isAnimating.current = false;
    };
  }, [score, lives]);

  const handleReset = () => {
    window.location.reload();
  };

  return (
    <div className="mt-8 flex flex-col items-center">
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        className="bg-black rounded shadow-md"
      />
      {(gameOver || gameClear) && (
        <div className="text-center mt-4 text-white">
          <p className="text-lg font-bold">
            {gameClear ? "🎉 CLEAR!!" : "😢 GAME OVER"}
          </p>
          <button
            onClick={handleReset}
            className="mt-2 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded"
          >
            Restart
          </button>
        </div>
      )}
    </div>
  );
}
