import { useLanguage } from "../context/LanguageContext";
import { useEffect, useState } from "react";

export default function Home() {
  const { language } = useLanguage();
  const [show, setShow] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setShow(true), 300); // 등장 애니메이션 지연
    return () => clearTimeout(timer);
  }, []);

  return (
    <main
      className="min-h-screen flex items-center justify-center relative overflow-hidden"
      style={{
        background: "linear-gradient(270deg, #c4dfff, #e0ccff, #ffe2e2)",
        backgroundSize: "600% 600%",
        animation: "aurora 10s ease infinite",
      }}
    >
      <style>
        {`
          @keyframes aurora {
            0%, 100% {
              background-position: 0% 50%;
            }
            50% {
              background-position: 100% 50%;
            }
          }

          .fade-in-up {
            opacity: 0;
            transform: translateY(20px);
            animation: fadeInUp 1s ease forwards;
          }

          @keyframes fadeInUp {
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
        `}
      </style>

      <div className={`text-center p-6 ${show ? "fade-in-up" : ""}`}>
        <h1 className="text-5xl font-bold mb-4 text-gray-900 dark:text-gray-100">Youngjee Park</h1>
        <p className="text-xl text-gray-700 dark:text-gray-300">
          {language === "en"
            ? "Passionate Developer & Creative Thinker"
            : "열정적인 개발자, 창의적인 사고를 가진 사람"}
        </p>
      </div>
    </main>
  );
}
