import React from "react";

const mindMapData = {
    label: "ME",
    color: "bg-lime-500",
    children: [
        {
            label: "Tech Stack",
            color: "bg-blue-400",
            children: [
                { label: "Java", color: "bg-blue-300" },
                { label: "Python", color: "bg-yellow-400" },
                { label: "Vue.js", color: "bg-green-400" },
                { label: "Django", color: "bg-emerald-400" }
            ]
        },
        {
            label: "Hobbies",
            color: "bg-orange-400",
            children: [
                { label: "Writing", color: "bg-pink-400" },
                { label: "Piano", color: "bg-red-300" },
                { label: "Game Making", color: "bg-violet-400" }
            ]
        },
        {
            label: "Languages",
            color: "bg-purple-700",
            children: [
                { label: "Korean", color: "bg-purple-500" },
                { label: "English", color: "bg-indigo-400" },
                { label: "Chinese", color: "bg-yellow-300" },
                { label: "Japanese", color: "bg-red-400" }
            ]
        }
    ]
};

export default function MindMap() {
    const renderNode = (node, index, parentX = 0, parentY = 0, depth = 0, totalSiblings = 1) => {
        const angle = (index / totalSiblings) * 2 * Math.PI - Math.PI / 2;
        const baseRadius = node.radius || (180 - depth * 40);
        const x = parentX + Math.cos(angle) * baseRadius;
        const y = parentY + Math.sin(angle) * baseRadius;

        return (
            <React.Fragment key={`${depth}-${index}-${node.label}`}>
                <div
                    className={`absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 w-24 h-24 rounded-full ${node.color} text-white flex items-center justify-center text-sm font-semibold shadow-md`}
                    style={{ transform: `translate(${x}px, ${y}px)` }}
                >
                    {node.label}
                </div>
                {node.children &&
                    node.children.map((child, childIndex) =>
                        renderNode(child, childIndex, x, y, depth + 1, node.children.length)
                    )}
            </React.Fragment>
        );
    };

    return (
        <div className="min-h-screen bg-white dark:bg-gray-900 flex items-center justify-center p-8">
            <div className="relative w-[700px] h-[700px]">
                {renderNode(mindMapData, 0, 0, 0, 0, 1)}
            </div>
        </div>
    );
}
