import React, { useRef, useEffect, useState } from 'react';

const BrickGirlRT = () => {
  const canvasRef = useRef(null);
  const spriteRef = useRef(new Image());

  // Ïä§ÌîÑÎùºÏù¥Ìä∏ ÏÑ§Ï†ï
  const frameWidth = 335;
  const frameHeight = 512;
  const numCols = 3;
  const numRows = 2;
  const scale = 0.2;

  const frameX = useRef(0);
  const frameY = useRef(0); // 0: Í±∑Í∏∞, 1: Ï†êÌîÑ ÏãúÎ°ú Î≥ÄÍ≤Ω ÏòàÏ†ï
  const frameCount = useRef(0);
  const [spriteLoaded, setSpriteLoaded] = useState(false);

  // Ï∫êÎ¶≠ÌÑ∞ ÏúÑÏπò Î∞è Ï†êÌîÑ Í¥ÄÎ†® Î≥ÄÏàò
  let girlX = 50;
  let girlY = 300;
  let isJumping = false;
  let velocityY = 0;
  const gravity = 0.8;

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    let animationFrameId;

    // Ï∫îÎ≤ÑÏä§ ÏÇ¨Ïù¥Ï¶à ÏÑ§Ï†ï
    canvas.width = 800;
    canvas.height = 400;

    // Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïù¥ÎØ∏ÏßÄ Î°úÎìú
    spriteRef.current.src = process.env.PUBLIC_URL + '/brickgirl_sprite.png';
    spriteRef.current.onload = () => {
      setSpriteLoaded(true);
    };

    // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏ Ï≤òÎ¶¨
    const handleKeyDown = (e) => {
      if (e.code === 'Space') {
        e.preventDefault(); // Ïä§ÌéòÏù¥Ïä§Î∞î Ïä§ÌÅ¨Î°§ Î∞©ÏßÄ
        throwBrick();
      } else if (e.code === 'ArrowUp') {
        if (!isJumping) {
          isJumping = true;
          velocityY = -12;
        }
      }
    };

    // Î™®Î∞îÏùº ÌÑ∞Ïπò Ï°∞Ïûë
    const handleTouchStart = (e) => {
      const touchX = e.touches[0].clientX;

      if (touchX < canvas.width / 2) {
        // ÏôºÏ™Ω ÌÑ∞Ïπò ‚Üí Ï†êÌîÑ
        if (!isJumping) {
          isJumping = true;
          velocityY = -12;
        }
      } else {
        // Ïò§Î•∏Ï™Ω ÌÑ∞Ïπò ‚Üí Í≥µÍ≤©
        throwBrick();
      }
    };

    // Ïï†ÎãàÎ©îÏù¥ÏÖò ÌîÑÎ†àÏûÑ ÏóÖÎç∞Ïù¥Ìä∏
    const updateFrame = () => {
      frameCount.current++;
      if (frameCount.current % 10 === 0) {
        frameX.current = (frameX.current + 1) % numCols;
      }
    };

    // Ï∫êÎ¶≠ÌÑ∞ Í∑∏Î¶¨Í∏∞
    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Ï§ëÎ†• Ï†ÅÏö© Î∞è Ï†êÌîÑ Ï≤òÎ¶¨
      if (isJumping) {
        velocityY += gravity;
        girlY += velocityY;

        if (girlY >= 300) {
          girlY = 300;
          isJumping = false;
          velocityY = 0;
        }
      }

      // Ïä§ÌîÑÎùºÏù¥Ìä∏ Í∑∏Î¶¨Í∏∞
      if (spriteLoaded) {
        updateFrame();
        ctx.drawImage(
          spriteRef.current,
          frameX.current * frameWidth,
          frameY.current * frameHeight,
          frameWidth,
          frameHeight,
          girlX,
          girlY,
          frameWidth * scale,
          frameHeight * scale
        );
      } else {
        // ÎåÄÏ≤¥ Ïù¥ÎØ∏ÏßÄ (Î°úÎî© Ï†Ñ)
        ctx.fillStyle = 'hotpink';
        ctx.fillRect(girlX, girlY, 30, 30);
      }

      animationFrameId = requestAnimationFrame(draw);
    };

    // Ï¥àÍ∏∞ ÏÑ§Ï†ï
    window.addEventListener('keydown', handleKeyDown);
    canvas.addEventListener('touchstart', handleTouchStart);
    draw();

    // Ï†ïÎ¶¨ Ìï®Ïàò
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      canvas.removeEventListener('touchstart', handleTouchStart);
      cancelAnimationFrame(animationFrameId);
    };
  }, [spriteLoaded]);

  // Î≤ΩÎèå ÎçòÏßÄÍ∏∞ Ìï®Ïàò (Ìè¨Î¨ºÏÑ† Íµ¨ÌòÑÏùÄ Ï∂îÌõÑ Ï∂îÍ∞Ä ÏòàÏ†ï)
  const throwBrick = () => {
    console.log('Î≤ΩÎèå ÎçòÏßÄÍ∏∞! (throwBrick Ïã§ÌñâÎê®)');
    // Ïó¨Í∏∞Ïóê Ìè¨Î¨ºÏÑ† Ìà¨ÏÇ¨Ï≤¥ Î°úÏßÅ Ï∂îÍ∞Ä ÏòàÏ†ï
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>Brick Girl: Run & Throw</h2>
      <canvas ref={canvasRef} style={{ border: '2px solid #999' }} />
      <p>‚¨ÜÔ∏è: Jump / Space: üß± (Î™®Î∞îÏùºÏùÄ ÌôîÎ©¥ Ï¢åÏö∞ ÌÑ∞Ïπò)</p>
    </div>
  );
};

export default BrickGirlRT;
