import React, { useState } from "react";

const mindMapData = {
  label: "ME",
  color: "bg-lime-500",
  children: [
    {
      label: "Tech Stack",
      color: "bg-blue-400",
      children: [
        { label: "Java", color: "bg-blue-300" },
        { label: "Python", color: "bg-blue-200" },
        { label: "JavaScript", color: "bg-blue-100" },
        { label: "HTML/CSS", color: "bg-blue-100" },
        { label: "C#", color: "bg-blue-100" },
        { label: "Vue.js", color: "bg-blue-200" },
        { label: "Django", color: "bg-blue-300" }
      ]
    },
    {
      label: "Hobbies",
      color: "bg-orange-400",
      children: [
        { label: "Writing", color: "bg-orange-300" },
        { label: "Piano", color: "bg-orange-200" },
        { label: "Game Making", color: "bg-orange-100" },
        { label: "Listening Music", color: "bg-orange-200" },
        { label: "Caligraphy", color: "bg-orange-200" },
        {
          label: "Game",
          color: "bg-orange-300",
          children: [
            { label: "Diablo", color: "bg-orange-200" },
            { label: "Animal Crossing", color: "bg-orange-100" },
            { label: "Mine Craft", color: "bg-orange-100" }
          ]
        }
      ]
    },
    {
      label: "Languages",
      color: "bg-purple-700",
      children: [
        { label: "Korean", color: "bg-purple-600" },
        { label: "English", color: "bg-purple-500" },
        { label: "Chinese", color: "bg-purple-400" },
        { label: "Japanese", color: "bg-purple-300" }
      ]
    }
  ]
};

export default function MindMapPage() {
  const [path, setPath] = useState([mindMapData]);

  const currentNode = path[path.length - 1];

  const handleClick = (node) => {
    if (node.children) {
      setPath([...path, node]);
    }
  };

  const handleBack = () => {
    if (path.length > 1) {
      setPath(path.slice(0, -1));
    }
  };

  const ripple = (e) => {
    const button = e.currentTarget;
    const ripple = document.createElement("span");
    ripple.className = "absolute rounded-full bg-white/40 animate-ping w-10 h-10";
    ripple.style.left = `${e.nativeEvent.offsetX - 20}px`;
    ripple.style.top = `${e.nativeEvent.offsetY - 20}px`;
    button.appendChild(ripple);
    setTimeout(() => ripple.remove(), 500);
  };

  const baseCircleClass =
    "rounded-full text-white text-center flex items-center justify-center shadow-xl border-4 border-white/20 dark:border-white/10 transition-all duration-300 bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-white/10 to-black/10";
  const hoverClass = "hover:scale-110 hover:brightness-110";

  const renderNodes = () => {
    return [
      path.length > 1 && (
        <button
          key="back-btn"
          onClick={handleBack}
          className={`w-16 h-16 ${currentNode.color} font-bold text-sm ${baseCircleClass} ${hoverClass} mb-4 mt-2`}
        >
          {currentNode.label}
        </button>
      ),
      ...(currentNode.children?.map((child) => {
        const hasChildren = !!child.children;
        return (
          <button
            key={child.label}
            className={`w-24 h-24 ${child.color} font-semibold ${baseCircleClass} ${hasChildren ? hoverClass : ""} relative overflow-hidden`}
            onClick={(e) => {
              if (hasChildren) {
                ripple(e);
                handleClick(child);
              }
            }}
          >
            {child.label}
          </button>
        );
      }) || [])
    ];
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-white flex flex-col items-center justify-start space-y-6 pt-24 pb-8 px-4">
      {renderNodes()}
    </div>
  );
}
