import { useEffect, useRef, useState } from "react";

export default function Game() {
  const canvasRef = useRef(null);
  const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

  // Î∏åÎ¶≠Í±∏ ÏÉÅÌÉú
  const girlX = 50;
  const girlYRef = useRef(0); // Ï†êÌîÑ ÏúÑÏπòÏö©
  const isJumping = useRef(false);
  const velocity = useRef(0);

  // ÏÑ†Ïù∏Ïû•
  const cactusXRef = useRef(canvasSize.width);
  const cactusY = canvasSize.height - 30;

  // Í≤åÏûÑ Î£®ÌîÑ
  const animationRef = useRef();

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    girlYRef.current = canvasSize.height - 40;

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Î∞∞Í≤Ω
      ctx.fillStyle = "#1e293b"; // Ïñ¥ÎëêÏö¥ Î∞§ Î∞∞Í≤Ω
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Î∏åÎ¶≠Í±∏
      ctx.font = "24px Arial";
      ctx.fillText("üß±", girlX, girlYRef.current);

      // ÏÑ†Ïù∏Ïû•
      ctx.font = "20px Arial";
      ctx.fillText("üåµ", cactusXRef.current, cactusY);

      // Ï†êÌîÑ Î°úÏßÅ
      if (isJumping.current) {
        girlYRef.current += velocity.current;
        velocity.current += 1; // gravity

        if (girlYRef.current >= canvasSize.height - 40) {
          girlYRef.current = canvasSize.height - 40;
          isJumping.current = false;
          velocity.current = 0;
        }
      }

      // ÏÑ†Ïù∏Ïû• Ïù¥Îèô
      cactusXRef.current -= 5;
      if (cactusXRef.current < -20) {
        cactusXRef.current = canvasSize.width + Math.random() * 200;
      }

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    const handleJump = () => {
      if (!isJumping.current) {
        isJumping.current = true;
        velocity.current = -15;
      }
    };

    window.addEventListener("keydown", handleJump);
    canvas.addEventListener("touchstart", handleJump);

    return () => {
      window.removeEventListener("keydown", handleJump);
      canvas.removeEventListener("touchstart", handleJump);
      cancelAnimationFrame(animationRef.current);
    };
  }, [canvasSize]);

  // Î∞òÏùëÌòï
  useEffect(() => {
    const updateSize = () => {
      const width = Math.min(window.innerWidth - 10, 480);
      const height = Math.round(width * 0.75);
      setCanvasSize({ width, height });
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  return (
    <div className="flex flex-col items-center mt-6">
      <h2 className="text-2xl font-bold mb-4">üß± Brick Girl Run</h2>
      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        className="bg-black rounded shadow-md"
        style={{ width: "100%", maxWidth: "480px" }}
      />
    </div>
  );
}
