import { useEffect, useRef, useState } from "react";

export default function BrickBreaker() {
  const canvasRef = useRef(null);

  const canvasWidth = 400;
  const canvasHeight = 300;
  const ballRadius = 10;

  const [score, setScore] = useState(0);
  const [highScore, setHighScore] = useState(
    parseInt(localStorage.getItem("highScore")) || 0
  );
  const [lives, setLives] = useState(3);
  const [gameOver, setGameOver] = useState(false);
  const [gameClear, setGameClear] = useState(false);

  let x = canvasWidth / 2;
  let y = canvasHeight - 30;
  let dx = 2;
  let dy = -2;

  const paddleHeight = 10;
  const paddleWidth = 75;
  let paddleX = (canvasWidth - paddleWidth) / 2;

  let rightPressed = false;
  let leftPressed = false;

  const brickRowCount = 3;
  const brickColumnCount = 6;
  const brickWidth = 50;
  const brickHeight = 20;
  const brickPadding = 10;
  const brickOffsetTop = 30;
  const brickOffsetLeft = 25;

  const bricks = [];
  for (let c = 0; c < brickColumnCount; c++) {
    bricks[c] = [];
    for (let r = 0; r < brickRowCount; r++) {
      bricks[c][r] = { x: 0, y: 0, status: 1 };
    }
  }

  const drawBall = (ctx) => {
    ctx.beginPath();
    ctx.arc(x, y, ballRadius, 0, Math.PI * 2);
    ctx.fillStyle = "#ff6347";
    ctx.fill();
    ctx.closePath();
  };

  const drawPaddle = (ctx) => {
    ctx.beginPath();
    ctx.rect(paddleX, canvasHeight - paddleHeight, paddleWidth, paddleHeight);
    ctx.fillStyle = "#0095DD";
    ctx.fill();
    ctx.closePath();
  };

  const drawBricks = (ctx) => {
    for (let c = 0; c < brickColumnCount; c++) {
      for (let r = 0; r < brickRowCount; r++) {
        const b = bricks[c][r];
        if (b.status === 1) {
          const brickX =
            c * (brickWidth + brickPadding) + brickOffsetLeft;
          const brickY =
            r * (brickHeight + brickPadding) + brickOffsetTop;
          b.x = brickX;
          b.y = brickY;

          ctx.beginPath();
          ctx.rect(brickX, brickY, brickWidth, brickHeight);
          ctx.fillStyle = "#fbbf24";
          ctx.fill();
          ctx.closePath();
        }
      }
    }
  };

  const drawScore = (ctx) => {
    ctx.font = "14px Arial";
    ctx.fillStyle = "#ffffff";
    ctx.fillText(`Score: ${score}`, 8, 20);
    ctx.fillText(`High: ${highScore}`, 8, 40);
  };

  const drawLives = (ctx) => {
    ctx.font = "14px Arial";
    ctx.fillStyle = "#ffffff";
    ctx.fillText(`Lives: ${lives}`, canvasWidth - 80, 20);
  };

  const collisionDetection = () => {
    let allBricksBroken = true;
    for (let c = 0; c < brickColumnCount; c++) {
      for (let r = 0; r < brickRowCount; r++) {
        const b = bricks[c][r];
        if (b.status === 1) {
          allBricksBroken = false;
          if (
            x > b.x &&
            x < b.x + brickWidth &&
            y > b.y &&
            y < b.y + brickHeight
          ) {
            dy = -dy;
            b.status = 0;
            const newScore = score + 1;
            setScore(newScore);
            if (newScore > highScore) {
              setHighScore(newScore);
              localStorage.setItem("highScore", newScore);
            }
          }
        }
      }
    }
    if (allBricksBroken) setGameClear(true);
  };

  const keyDownHandler = (e) => {
    if (e.key === "Right" || e.key === "ArrowRight") rightPressed = true;
    else if (e.key === "Left" || e.key === "ArrowLeft") leftPressed = true;
  };

  const keyUpHandler = (e) => {
    if (e.key === "Right" || e.key === "ArrowRight") rightPressed = false;
    else if (e.key === "Left" || e.key === "ArrowLeft") leftPressed = false;
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    document.addEventListener("keydown", keyDownHandler, false);
    document.addEventListener("keyup", keyUpHandler, false);

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      drawScore(ctx);
      drawLives(ctx);
      drawBricks(ctx);
      drawBall(ctx);
      drawPaddle(ctx);
      collisionDetection();

      if (x + dx > canvas.width - ballRadius || x + dx < ballRadius) dx = -dx;
      if (y + dy < ballRadius) dy = -dy;
      else if (y + dy > canvas.height - ballRadius) {
        if (x > paddleX && x < paddleX + paddleWidth) {
          dy = -dy;
        } else {
          setLives((prev) => {
            const remaining = prev - 1;
            if (remaining <= 0) setGameOver(true);
            return remaining;
          });
          x = canvasWidth / 2;
          y = canvasHeight - 30;
          dx = 2;
          dy = -2;
          paddleX = (canvasWidth - paddleWidth) / 2;
        }
      }

      if (rightPressed && paddleX < canvas.width - paddleWidth)
        paddleX += 5;
      else if (leftPressed && paddleX > 0)
        paddleX -= 5;

      x += dx;
      y += dy;

      if (!gameOver && !gameClear) requestAnimationFrame(draw);
    };

    draw();

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
      document.removeEventListener("keyup", keyUpHandler);
    };
  }, [score, lives]);

  const handleReset = () => {
    window.location.reload();
  };

  return (
    <div className="mt-8 flex flex-col items-center">
      <canvas
        ref={canvasRef}
        width={canvasWidth}
        height={canvasHeight}
        className="bg-black rounded shadow-md"
      />
      {(gameOver || gameClear) && (
        <div className="text-center mt-4 text-white">
          <p className="text-lg font-bold">
            {gameClear ? "ðŸŽ‰ CLEAR!!" : "ðŸ˜¢ GAME OVER"}
          </p>
          <button
            onClick={handleReset}
            className="mt-2 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded"
          >
            Restart
          </button>
        </div>
      )}
    </div>
  );
}
