            const tileBottom = tile.y;
            return (
                feetX > tile.x &&
                feetX < tile.x + tileSize &&
                feetY >= tileBottom &&
                feetY <= tileBottom + 10 // ÏûëÏùÄ Ïó¨Ïú†
            );
        });
    };

