        const handleKeyDown = (e) => {
            if (e.code === 'Space') {
                e.preventDefault(); // Ïä§ÌéòÏù¥Ïä§Î∞î Ïä§ÌÅ¨Î°§ Î∞©ÏßÄ
                throwBrick();
            } else if (e.code === 'ArrowUp') {
                if (jumpCount < maxJumps) {
                    velocityY = -12 * (jumpCount === 1 ? 1.2 : 1); // 2Îã® Ï†êÌîÑÎäî Îçî ÎÜíÏù¥
                    isJumping = true;
                    jumpCount++;
                }
            }
        };

