                ctx.fillRect(tile.x, tile.y, tileSize, tileSize);
            });
            if (girlY > canvas.height) {
                console.log('Game Over!');
                // Î¶¨ÏÖã Ï≤òÎ¶¨ or Ï¢ÖÎ£å
            }

            // Ï∞©ÏßÄ Î°úÏßÅ
