const careerTimeline = [
    {
      company: "Redcap Tour (Seoul, Korea)",
      period: "2023 – 2024",
      description: "호텔 & 에이전시 관리 시스템 개발. 백엔드 API 최적화 및 Oracle 통합."
    },
    {
      company: "KGC Taiwan (Taipei, Taiwan)",
      period: "2022 – 2023",
      description: "데이터 관리 시스템 개발 및 AWS 배포. 자동화된 워크플로우 구축."
    },
    {
      company: "Freelance Projects",
      period: "2020 – 2021",
      description: "Line Bank, Lotte Mart, Doosan Infracore 등 다양한 기업 프로젝트 진행."
    },
    {
      company: "eBay Korea (Seoul, Korea)",
      period: "2018 – 2019",
      description: "항공권 플랫폼 개발 및 API 안정성 향상."
    },
    {
      company: "Ariel Networks (Seoul, Korea)",
      period: "2017 – 2018",
      description: "3D 콘텐츠 개발 및 KT 독도 프로젝트 참여."
    },
    {
      company: "Sejong University R&D (Seoul, Korea)",
      period: "2017",
      description: "천문학 시각화 연구 도구 및 웹 인터페이스 개발."
    }
  ];
  
  export default function Career() {
    return (
      <main className="min-h-screen bg-white text-gray-800 dark:bg-gray-900 dark:text-gray-100 p-6">
        <h1 className="text-4xl font-bold text-center mb-10">Career Timeline</h1>
        <div className="max-w-4xl mx-auto space-y-6">
          {careerTimeline.map((job, index) => (
            <div key={index} className="border-l-4 border-gray-400 pl-4">
              <h2 className="text-xl font-semibold">{job.company}</h2>
              <p className="text-sm text-gray-500 mb-1">{job.period}</p>
              <p className="text-gray-700">{job.description}</p>
            </div>
          ))}
        </div>
      </main>
    );
  }
