import React, { useState } from "react";
import { motion } from "framer-motion"; // ✅ framer-motion 추가

// 전체 MindMap 데이터 구조
const mindMapData = {
  label: "ME",
  color: "bg-lime-500",
  children: [
    {
      label: "Tech Stack",
      color: "bg-blue-400",
      children: [
        { label: "Java", color: "bg-blue-300" },
        { label: "JavaScript", color: "bg-blue-300" },
        { label: "HTML/CSS", color: "bg-blue-200" },
        { label: "Python", color: "bg-blue-200" },
        { label: "C#", color: "bg-blue-200" },
        { label: "Vue.js", color: "bg-blue-100" },
        { label: "Django", color: "bg-blue-100" }
      ]
    },
    {
      label: "Hobbies",
      color: "bg-orange-400",
      children: [
        {
          label: "Game",
          color: "bg-orange-300",
          children: [
            { label: "Diablo", color: "bg-orange-200" },
            { label: "Animal Crossing", color: "bg-orange-100" },
            { label: "Mine Craft", color: "bg-orange-100" }
          ]
        },
        {
          label: "Writing", color: "bg-orange-300",
          children: [
            { label: "Essay", color: "bg-orange-200" },
            { label: "Novel", color: "bg-orange-100" }
          ]
        },
        {
          label: "Listening Music",
          color: "bg-orange-300",
          children: [
            { label: "Classical Music", color: "bg-orange-200" },
            { label: "Jazz", color: "bg-orange-200" },
            { label: "K-pop", color: "bg-orange-100" }
          ]
        },
        { label: "Piano", color: "bg-orange-200" },
        { label: "Game Making", color: "bg-orange-100" },
        { label: "Caligraphy", color: "bg-orange-100" }
      ]
    },
    {
      label: "Languages",
      color: "bg-purple-700",
      children: [
        { label: "Korean ⭐⭐⭐", color: "bg-purple-600" },
        { label: "English ⭐⭐", color: "bg-purple-500" },
        { label: "Chinese ⭐⭐", color: "bg-purple-400" },
        { label: "Japanese ⭐", color: "bg-purple-300" }
      ]
    }
  ]
};

export default function MindMapPage() {
  const [path, setPath] = useState([]); // 클릭 경로 추적
  const currentNode = path[path.length - 1] || mindMapData; // 현재 노드
  const isRoot = path.length === 0;

  // 🎯 ME 위치 (중앙보다 살짝 위)
  const mePosition = {
    x: window.innerWidth / 2 - 70,
    y: window.innerHeight / 2 - 150
  };

  // 🎯 하위 노드 중심 위치 (조금 아래)
  const baseCenter = {
    x: window.innerWidth / 2 - 55,
    y: window.innerHeight / 2 - 130
  };

  // ✅ 클릭 시 상위 노드 진입 또는 뒤로가기 처리
  const handleClick = (node) => {
    if (node === currentNode && path.length > 0) {
      setPath((prev) => prev.slice(0, -1)); // 뒤로 가기
    } else if (node.children) {
      setPath((prev) => [...prev, node]); // 다음 단계 진입
    }
  };

  // 공통 버튼 스타일
  const baseCircleClass =
    "absolute rounded-full text-white text-center flex items-center justify-center shadow-xl border-4 border-white/20 dark:border-white/10 bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-white/10 to-black/10";

  // 방사형 배치 함수
  const getRadialLayout = (count, radius = 140) => {
    const angleStep = (Math.PI * 2) / count;
    return Array.from({ length: count }, (_, i) => {
      const angle = angleStep * i - Math.PI / 2;
      return {
        x: baseCenter.x + Math.cos(angle) * radius,
        y: baseCenter.y + Math.sin(angle) * radius
      };
    });
  };

  return (
    <div className="relative w-screen h-screen bg-white dark:bg-gray-900 overflow-hidden">
      {/* ✅ 상위 노드 (ME 또는 클릭한 항목) */}
      <motion.button
        key={currentNode.label + "-main"} // key 변경해야 애니메이션 적용됨
        initial={{ opacity: 0, scale: 0.9 }} // 시작 상태
        animate={{ opacity: 1, scale: 1, left: mePosition.x, top: mePosition.y }} // 애니메이션 도착
        transition={{ duration: 0.4 }}
        className={`w-28 h-28 ${currentNode.color} font-bold text-xl z-10 ${baseCircleClass}`}
        onClick={() => handleClick(currentNode)}
      >
        {currentNode.label}
      </motion.button>

      {/* ✅ 하위 노드 (방사형 배치) */}
      {!isRoot &&
        currentNode.children &&
        getRadialLayout(currentNode.children.length).map((pos, i) => {
          const child = currentNode.children[i];
          return (
            <button
              key={child.label}
              style={{ left: pos.x, top: pos.y }}
              className={`w-20 h-20 ${child.color} font-semibold z-0 ${baseCircleClass}`}
              onClick={() => handleClick(child)}
            >
              {child.label}
            </button>
          );
        })}
    </div>
  );
}
