    const [gameClear, setGameClear] = useState(false);

    // Ï¥àÍ∏∞ Î≥ÑÎπõ ÏÉùÏÑ±
    useEffect(() => {
        const stars = Array.from({ length: 100 }, () => ({
            x: Math.random() * canvasSize.width,
            y: Math.random() * canvasSize.height,
            size: Math.random() * 0.8 + 0.2,
            speed: Math.random() * 1 + 0.5,
        }));
        starsRef.current = stars;
    }, [canvasSize]);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        // Î∏åÎ¶≠Í±∏ Ïù¥ÎØ∏ÏßÄ Î∂àÎü¨Ïò§Í∏∞
        const sprite = new Image();
        sprite.src = "/brickgirl_sprite.png";

        sprite.onload = () => {
            draw(); // Ïó¨Í∏∞Ïóê Í≤åÏûÑ Î£®ÌîÑ ÏãúÏûë
        };

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Î∞∞Í≤Ω Ïä§ÌÅ¨Î°§ Ìö®Í≥º
            ctx.fillStyle = "black";
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            ctx.fillStyle = "white";
            starsRef.current.forEach((star) => {
                star.x -= star.speed;
                if (star.x < 0) star.x = canvas.width;
                ctx.beginPath();
                ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
                ctx.fill();
            });

            if (gameOver) {
                const centerX = canvasRef.current.width / 2;
                const centerY = canvasRef.current.height / 2;

                ctx.font = "24px Arial";
                ctx.fillStyle = "white";
                ctx.textAlign = "center";
                ctx.fillText("üò¢ GAME OVER", centerX, centerY - 20);

                // restart message
                ctx.font = "18px Arial";
                ctx.fillStyle = "#818cf8";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 10);
                ctx.fillText("üîÑ", centerX, centerY + 40);
                return;
            }

            if (gameClear) {
                ctx.font = "24px Arial";
                ctx.fillStyle = "white";
                ctx.textAlign = "center";
                ctx.fillText("üéâ MISSION COMPLETE!", canvasRef.current.width / 2, canvasRef.current.height / 2 - 20);
                ctx.font = "18px Arial";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", canvasRef.current.width / 2, canvasRef.current.height / 2 + 10);
                ctx.fillText("üîÑ", canvasRef.current.width / 2, canvasRef.current.height / 2 + 40);
                return;
            }

            // Î∏åÎ¶≠Í±∏
            // ctx.font = "20px Arial";
            // ctx.fillText("üß±", girlX, girlYRef.current);
            ctx.drawImage(
                sprite,
                frameX.current * frameWidth,
                frameY.current * frameHeight,
                frameWidth,
                frameHeight,
                girlX,
                girlYRef.current,
                frameWidth,
                frameHeight
            );

            // Ïï†ÎãàÎ©îÏù¥ÏÖò ÏÜçÎèÑ Ï°∞Ï†à
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
                if (frameX.current === 0) {
                  frameY.current = (frameY.current + 1) % numRows;
                }
              }
              frameCount.current++;

            // Î∏åÎ¶≠Í±∏ Ïù¥Îèô
            if (keys.current.up && girlYRef.current > 20) girlYRef.current -= girlSpeed;
            if (keys.current.down && girlYRef.current < canvas.height - 20) girlYRef.current += girlSpeed;

            // Î∏åÎ¶≠ Ïù¥Îèô Î∞è Í∑∏Î¶¨Í∏∞
            ctx.font = "16px Arial";
            ctx.fillStyle = "#f87171";
            bricksRef.current.forEach((brick, brickIdx) => {
                brick.x += brick.speed;
                ctx.fillText("üß±", brick.x, brick.y);

                // Ï†ÅÍ≥º Ï∂©Îèå Ï≤¥ÌÅ¨
                enemiesRef.current.forEach((enemy, enemyIdx) => {
                    if (
                        brick.x > enemy.x - 10 &&
                        brick.x < enemy.x + 10 &&
                        brick.y > enemy.y - 10 &&
                        brick.y < enemy.y + 10
                    ) {
                        enemiesRef.current.splice(enemyIdx, 1);
                        bricksRef.current.splice(brickIdx, 1);
                        scoreRef.current += 1;
                        setScore(scoreRef.current);
                        if (scoreRef.current >= 50) setGameClear(true);
                    }
                });

                if (brick.x > canvas.width) {
                    bricksRef.current.splice(brickIdx, 1);
                }
            });

            // Ï†Å Ïö∞Ï£ºÏÑ† Ïù¥Îèô & Í∑∏Î¶¨Í∏∞
            ctx.font = "20px Arial";
            ctx.fillStyle = "#f472b6";
            enemiesRef.current.forEach((enemy, index) => {
                enemy.x -= enemy.speed;
                // ctx.fillText("üöÄ", enemy.x, enemy.y);
                ctx.fillText(enemy.emoji, enemy.x, enemy.y);

                // Î∏åÎ¶≠Í±∏Í≥º Ï∂©Îèå Ïãú Í≤åÏûÑ Ïò§Î≤Ñ
                if (
                    enemy.x < girlX + 10 &&
                    enemy.x > girlX - 10 &&
                    enemy.y > girlYRef.current - 10 &&
                    enemy.y < girlYRef.current + 10
                ) {
                    setGameOver(true);
                }

                // Ï†ÅÏù¥ ÌôîÎ©¥ ÏôºÏ™Ω ÎÅùÍπåÏßÄ ÎèÑÎã¨ÌïòÎ©¥ -1Ï†ê Ï≤òÎ¶¨
                if (enemy.x < -30) {
                    enemiesRef.current.splice(index, 1);
                    scoreRef.current = Math.max(0, scoreRef.current - 1); // Ï†êÏàò Í∞êÏÜå (ÏµúÏÜå 0)
                    setScore(scoreRef.current);
                }
            });

            // Ï†êÏàò ÌëúÏãú
            ctx.fillStyle = "#fbbf24";
            ctx.font = "16px Arial";
            ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);

            animationRef.current = requestAnimationFrame(draw);
        };

        draw();

        const handleKeyDown = (e) => {
            if (e.key === "ArrowUp") keys.current.up = true;
            if (e.key === "ArrowDown") keys.current.down = true;
            if (e.key === " " && !shootCooldownRef.current && !gameOver) {
                bricksRef.current.push({ x: girlX + 10, y: girlYRef.current, speed: 6 });
                shootCooldownRef.current = true;
                setTimeout(() => (shootCooldownRef.current = false), 300);
            }
            if ((e.key === "r" || e.key === "R") && gameOver) {
                window.location.reload();
            }
        };

        const handleKeyUp = (e) => {
            if (e.key === "ArrowUp") keys.current.up = false;
            if (e.key === "ArrowDown") keys.current.down = false;
        };

        const handleTouch = () => {
            if (gameOver || gameClear) {
                window.location.reload();
            }
        };

        // Î™®Î∞îÏùº ÌÑ∞Ïπò Ïù¥Î≤§Ìä∏
        let touchStartY = 0;

        const onTouchStart = (e) => {
            touchStartY = e.touches[0].clientY;

            // Í≤åÏûÑ Ïò§Î≤Ñ ÎòêÎäî ÌÅ¥Î¶¨Ïñ¥ ÏÉÅÌÉúÏóêÏÑú ÌÉ≠ Ïãú Ïû¨ÏãúÏûë
            if (gameOver || gameClear) {
                window.location.reload();
                return;
            }

            // Î∏åÎ¶≠Í±∏ Îî∞ÎùºÍ∞ÄÍ∏∞: ÌÑ∞ÏπòÎêú ÏúÑÏπòÎ°ú Ïù¥Îèô
            const touchY = touchStartY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 20), canvas.height - 20);

            // Ïó∞ÏÇ¨ ÏãúÏûë
            if (!shootIntervalRef.current && !gameOver && !gameClear) {
                shootIntervalRef.current = setInterval(() => {
                    bricksRef.current.push({ x: girlX + 10, y: girlYRef.current, speed: 6 });
                }, 250);
            }
        };

        const onTouchMove = (e) => {
            const touchY = e.touches[0].clientY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 20), canvas.height - 20);
        };

        const onTouchEnd = () => {
            clearInterval(shootIntervalRef.current);
            shootIntervalRef.current = null;
        };

        // key Ïù¥Î≤§Ìä∏ Î∞è touch Ïù¥Î≤§Ìä∏
        canvas.addEventListener("touchstart", onTouchStart, { passive: false });
        canvas.addEventListener("touchmove", onTouchMove, { passive: false });
        canvas.addEventListener("touchend", onTouchEnd, { passive: false });

        window.addEventListener("keydown", handleKeyDown);
        window.addEventListener("keyup", handleKeyUp);

        return () => {
            window.removeEventListener("keydown", handleKeyDown);
            window.removeEventListener("keyup", handleKeyUp);
            canvas.removeEventListener("touchstart", onTouchStart);
            canvas.removeEventListener("touchmove", onTouchMove);
            canvas.removeEventListener("touchend", onTouchEnd);
            cancelAnimationFrame(animationRef.current);
            clearInterval(shootIntervalRef.current);
        };
    }, [canvasSize, gameOver, gameClear]);

    useEffect(() => {
        enemySpawnInterval.current = setInterval(() => {
            if (!gameOver && !gameClear) {
                // üëΩ üöÄ üõ∏ Ï§ë ÌïòÎÇò Î¨¥ÏûëÏúÑÎ°ú ÏÑ†ÌÉù
                const emojis = ["üöÄ", "üëΩ", "üõ∏"];
                const randomEmoji = emojis[Math.floor(Math.random() * emojis.length)];

                enemiesRef.current.push({
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 20) + 10,
                    speed: Math.random() * 1.5 + 1.5,
                    emoji: randomEmoji,
                });
            }
        }, 1500);
        return () => clearInterval(enemySpawnInterval.current);
    }, [canvasSize, gameOver, gameClear]);

    useEffect(() => {
        const updateSize = () => {
            const width = Math.min(window.innerWidth - 10, 480);
            const height = Math.round(width * 0.75);
            setCanvasSize({ width, height });
        };
        updateSize();
        window.addEventListener("resize", updateSize);
        return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
        <div className="flex flex-col items-center mt-6">
            <h2 className="text-2xl font-bold mb-4">üß± Brick Girl: Space Defense</h2>
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                className="bg-black rounded shadow-md touch-none"
                style={{ width: "100%", maxWidth: "480px" }}
            />
        </div>
    );
}
