import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useLanguage } from "../context/LanguageContext";

// Í≥†Ï†ïÎêú ÌÉ≠ key Î™©Î°ù
const tabKeys = ["miniGames", "brickGirl", "novel"];

// Îã§Íµ≠Ïñ¥ ÌÉ≠ ÎùºÎ≤®
const tabLabels = {
  en: {
    miniGames: "Mini Games",
    brickGirl: "Brick Girl",
    novel: "Novel"
  },
  ko: {
    miniGames: "ÎØ∏ÎãàÍ≤åÏûÑ",
    brickGirl: "Î∏åÎ¶≠Í±∏",
    novel: "ÏÜåÏÑ§"
  }
};

// ÏΩòÌÖêÏ∏† Îç∞Ïù¥ÌÑ∞
const tabContent = {
  miniGames: [
    {
      title: { en: "Brick Breaker", ko: "Î≤ΩÎèåÍπ®Í∏∞" },
      description: {
        en: "A classic brick-breaking mini game recreated with React + Canvas. Break all the blocks and challenge your reflexes!",
        ko: "ÌÅ¥ÎûòÏãù Î≤ΩÎèåÍπ®Í∏∞ Í≤åÏûÑÏùÑ React + CanvasÎ°ú Ïû¨ÌòÑÌñàÏäµÎãàÎã§. Î™®Îì† Î≤ΩÎèåÏùÑ Î∂ÄÏàòÎ©∞ Î∞òÏÇ¨Ïã†Í≤ΩÏùÑ ÏãúÌóòÌï¥Î≥¥ÏÑ∏Ïöî!"
      },
      status: { en: "Playable", ko: "ÌîåÎ†àÏù¥ Í∞ÄÎä•" },
      link: "/portfolio#/bb"
    },
    {
      title: { en: "Brick Girl: Space Defense", ko: "Î∏åÎ¶≠Í±∏: Ïä§ÌéòÏù¥Ïä§ ÎîîÌéúÏä§" },
      description: {
        en: "Defend space against alien invaders by throwing bricks! A fast-paced shooter built with React + Canvas.",
        ko: "Ïö∞Ï£º Ïπ®Í≥µÏùÑ ÎßâÏïÑÎùº! Î≤ΩÎèåÏùÑ ÎçòÏ†∏ Ïô∏Í≥ÑÏù∏ÏùÑ Ï≤òÏπòÌïòÎäî ÏäàÌåÖÌòï ÎØ∏Îãà Í≤åÏûÑÏûÖÎãàÎã§. React + CanvasÎ°ú Ï†úÏûë."
      },
      status: { en: "Playable", ko: "ÌîåÎ†àÏù¥ Í∞ÄÎä•" },
      link: "/portfolio#/bsd"
    },
    {
      title: { en: "Brick Girl: Run & Throw", ko: "Î∏åÎ¶≠Í±∏: Îü∞ & Ïä§Î°úÏö∞" },
      description: {
        en: "A runner-style game where you break through obstacles with bricks while running. Built with React + Canvas.",
        ko: "Îã¨Î¶¨Î©∞ Î≤ΩÎèåÎ°ú Ïû•Ïï†Î¨ºÏùÑ Ï†úÍ±∞ÌïòÎäî Îü¨ÎÑà Ïä§ÌÉÄÏùº ÎØ∏Îãà Í≤åÏûÑÏûÖÎãàÎã§. React + CanvasÎ°ú Ï†úÏûë."
      },
      status: { en: "In Progress", ko: "ÏûëÏóÖ Ï§ë" },
      link: "" // ÎßÅÌÅ¨ ÏóÜÏùå
    }
  ],
  brickGirl: [
    {
      title: { en: "Brick Girl Universe", ko: "Î∏åÎ¶≠Í±∏ Ïú†ÎãàÎ≤ÑÏä§" },
      description: {
        en: "A pixel-art story game concept set in space.",
        ko: "ÌîΩÏÖÄ ÏïÑÌä∏ Í∏∞Î∞òÏùò Ïö∞Ï£º ÏÑ∏Í≥ÑÍ¥Ä Ïä§ÌÜ†Î¶¨ Í≤åÏûÑ Í∏∞ÌöçÏûÖÎãàÎã§."
      },
      status: { en: "In Progress", ko: "ÏûëÏóÖ Ï§ë" },
      link: ""
    }
  ],
  novel: [
    {
      title: { en: "„ÄéYou Are Not on Earth„Äè", ko: "„ÄéÎÑàÎäî ÏßÄÍµ¨Ïóê ÏóÜÏñ¥„Äè" },
      description: {
        en: "A romance fantasy novel project in progress.",
        ko: "Î°úÎß®Ïä§ ÌåêÌÉÄÏßÄ Ïû•Ìé∏ÏÜåÏÑ§ ÏûëÏóÖ Ï§ëÏûÖÎãàÎã§."
      },
      status: { en: "Coming Soon", ko: "Ïó∞Ïû¨ Ï§ÄÎπÑÏ§ë" },
      link: ""
    }
  ]
};

export default function Lab() {
  const { language } = useLanguage();
  const [activeTab, setActiveTab] = useState("miniGames");

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-800 dark:text-gray-100 px-4 py-16">
      {/* ÌÉÄÏù¥ÌãÄ */}
      <h1 className="text-4xl font-bold text-center mb-10">
        üß™ {language === "en" ? "Lab: My Creative Playground" : "Ïã§ÌóòÏã§: ÎÇòÎßåÏùò Ï∞ΩÏûë Í≥µÍ∞Ñ"}
      </h1>

      {/* ÌÉ≠ Î≤ÑÌäºÎì§ */}
      <div className="flex justify-center gap-4 mb-8 flex-wrap">
        {tabKeys.map((key) => (
          <button
            key={key}
            onClick={() => setActiveTab(key)}
            className={`px-4 py-2 rounded-full text-sm font-medium transition-all ${
              activeTab === key
                ? "bg-cyan-500 text-white shadow"
                : "bg-gray-200 dark:bg-zinc-700 text-gray-700 dark:text-gray-300 hover:bg-gray-300 dark:hover:bg-zinc-600"
            }`}
          >
            {tabLabels[language][key]}
          </button>
        ))}
      </div>

      {/* ÏΩòÌÖêÏ∏† Ïπ¥Îìú */}
      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        <AnimatePresence mode="wait">
          {tabContent[activeTab]?.map((item, i) => {
            const isClickable = !!item.link;

            const ContentWrapper = isClickable ? motion.a : motion.div;
            const baseStyle = `block p-6 rounded-xl border transition-all ${
              isClickable
                ? "cursor-pointer hover:shadow-lg dark:hover:shadow-white/10"
                : "opacity-50 cursor-not-allowed select-none"
            }`;

            return (
              <ContentWrapper
                key={item.title.en}
                {...(isClickable && {
                  href: item.link,
                  target: "_blank",
                  rel: "noopener noreferrer"
                })}
                initial={{ opacity: 0, y: 30 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: 20 }}
                transition={{ duration: 0.4, delay: i * 0.1 }}
                className={`${baseStyle} bg-white dark:bg-zinc-800 shadow-md border-gray-200 dark:border-white/10`}
              >
                <h2 className="text-xl font-semibold mb-2 text-indigo-500 dark:text-indigo-300">
                  {item.title[language]}
                </h2>
                <p className="text-sm mb-2">{item.description[language]}</p>
                <span className="text-xs px-3 py-1 inline-block rounded-full bg-indigo-100 dark:bg-indigo-800 text-indigo-600 dark:text-indigo-200">
                  {item.status[language]}
                </span>
              </ContentWrapper>
            );
          })}
        </AnimatePresence>
      </div>
    </div>
  );
}
