import { useEffect, useRef, useState } from "react";

export default function Game() {
  const canvasRef = useRef(null);
  const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

  // 브릭걸 상태
  const girlX = 30;
  const girlYRef = useRef(150);
  const girlSpeed = 4;
  const keys = useRef({ up: false, down: false });

  // 적 우주선
  const enemiesRef = useRef([]);
  const enemySpawnInterval = useRef(null);

  // 애니메이션 루프
  const animationRef = useRef();

  // 배경 별빛 좌표
  const starsRef = useRef([]);

  // 초기 별빛 생성
  useEffect(() => {
    const stars = Array.from({ length: 100 }, () => ({
      x: Math.random() * canvasSize.width,
      y: Math.random() * canvasSize.height,
      size: Math.random() * 2 + 1,
      speed: Math.random() * 1 + 0.5,
    }));
    starsRef.current = stars;
  }, [canvasSize]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 배경 스크롤 효과
      ctx.fillStyle = "black";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = "white";
      starsRef.current.forEach((star) => {
        star.x -= star.speed;
        if (star.x < 0) star.x = canvas.width;
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
        ctx.fill();
      });

      // 브릭걸
      ctx.font = "20px Arial";
      ctx.fillText("🧱", girlX, girlYRef.current);

      // 브릭걸 이동
      if (keys.current.up && girlYRef.current > 20) girlYRef.current -= girlSpeed;
      if (keys.current.down && girlYRef.current < canvas.height - 20) girlYRef.current += girlSpeed;

      // 적 우주선 이동 & 그리기
      ctx.font = "20px Arial";
      ctx.fillStyle = "#f472b6";
      enemiesRef.current.forEach((enemy, index) => {
        enemy.x -= enemy.speed;
        ctx.fillText("🚀", enemy.x, enemy.y);

        // 화면 벗어나면 제거
        if (enemy.x < -30) {
          enemiesRef.current.splice(index, 1);
        }
      });

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    const handleKeyDown = (e) => {
      if (e.key === "ArrowUp") keys.current.up = true;
      if (e.key === "ArrowDown") keys.current.down = true;
    };

    const handleKeyUp = (e) => {
      if (e.key === "ArrowUp") keys.current.up = false;
      if (e.key === "ArrowDown") keys.current.down = false;
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);

    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
      cancelAnimationFrame(animationRef.current);
    };
  }, [canvasSize]);

  // 적 우주선 생성 주기 설정
  useEffect(() => {
    enemySpawnInterval.current = setInterval(() => {
      enemiesRef.current.push({
        x: canvasSize.width + 20,
        y: Math.random() * (canvasSize.height - 20) + 10,
        speed: Math.random() * 1.5 + 1.5,
      });
    }, 1500); // 1.5초마다 등장

    return () => clearInterval(enemySpawnInterval.current);
  }, [canvasSize]);

  // 반응형 처리
  useEffect(() => {
    const updateSize = () => {
      const width = Math.min(window.innerWidth - 10, 480);
      const height = Math.round(width * 0.75);
      setCanvasSize({ width, height });
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  return (
    <div className="flex flex-col items-center mt-6">
      <h2 className="text-2xl font-bold mb-4">🧱 Brick Girl: Space Defense</h2>
      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        className="bg-black rounded shadow-md"
        style={{ width: "100%", maxWidth: "480px" }}
      />
    </div>
  );
}
