import React from "react";
import { motion } from "framer-motion";

const labItems = [
  {
    title: "Brick Girl Mini Game",
    description: "A pixel-art runner game prototype made with React + Canvas.",
    status: "In Progress",
    link: "/game/brickgirl"
  },
  {
    title: "Mind Map Navigation",
    description: "Animated self-introduction through radial mind map UX.",
    status: "Complete",
    link: "/mindmap"
  },
  {
    title: "Lab Project 3",
    description: "This is where your next experiment goes!",
    status: "Coming Soon",
    link: "#"
  }
];

export default function Lab() {
  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-800 dark:text-gray-100 px-4 py-16">
      <h1 className="text-4xl font-bold text-center mb-10">
        ðŸ§ª Lab: My Creative Playground
      </h1>
      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3 max-w-6xl mx-auto">
        {labItems.map((item, i) => (
          <motion.a
            key={i}
            href={item.link}
            target="_blank"
            rel="noopener noreferrer"
            className="block p-6 rounded-xl bg-white dark:bg-zinc-800 shadow-md dark:shadow-white/5 hover:shadow-lg dark:hover:shadow-white/10 transition-all border border-gray-200 dark:border-white/10"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: i * 0.1, duration: 0.4 }}
          >
            <h2 className="text-xl font-semibold mb-2 text-indigo-500 dark:text-indigo-300">
              {item.title}
            </h2>
            <p className="text-sm mb-2">{item.description}</p>
            <span className="text-xs px-3 py-1 inline-block rounded-full bg-indigo-100 dark:bg-indigo-800 text-indigo-600 dark:text-indigo-200">
              {item.status}
            </span>
          </motion.a>
        ))}
      </div>
    </div>
  );
}
