    const numCols = 3;  // ì´ë¯¸ì§€ ê°€ë¡œ í”„ë ˆìž„ ìˆ˜
    const numRows = 2;  // ì´ë¯¸ì§€ ì„¸ë¡œ í”„ë ˆìž„ ìˆ˜
