    // ë³„ë¹› ì´ˆê¸°í™”
    useEffect(() => {
        const stars = Array.from({ length: 100 }, () => ({
            x: Math.random() * canvasSize.width,
            y: Math.random() * canvasSize.height,
            size: Math.random() * 0.8 + 0.2,
            speed: Math.random() * 1 + 0.5,
        }));
        starsRef.current = stars;
    }, [canvasSize]);

    // ë¸Œë¦­ê±¸ ìŠ¤í”„ë¼ì´íŠ¸ ì´ë¯¸ì§€ ë¡œë“œ
    useEffect(() => {
        const sprite = spriteRef.current;
        sprite.src = process.env.PUBLIC_URL + "/brickgirl_sprite.png";  // public í´ë” ê²½ë¡œ ê¸°ì¤€
        sprite.onload = () => {
            setSpriteLoaded(true);  // ì´ë¯¸ì§€ ë¡œë“œ ì™„ë£Œ
        };
    }, []);

    // ê²Œìž„ ë£¨í”„ ì‹¤í–‰
    useEffect(() => {
        if (!spriteLoaded) return;  // ì´ë¯¸ì§€ê°€ ì•„ì§ ë¡œë“œë˜ì§€ ì•Šìœ¼ë©´ ì‹¤í–‰ X

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // ë°°ê²½ ë³„ë¹›
            ctx.fillStyle = "black";
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "white";
            starsRef.current.forEach((star) => {
                star.x -= star.speed;
                if (star.x < 0) star.x = canvas.width;
                ctx.beginPath();
                ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
                ctx.fill();
            });

            // ê²Œìž„ ì¢…ë£Œ í™”ë©´
            if (gameOver || gameClear) {
                const centerX = canvas.width / 2;
                const centerY = canvas.height / 2;
                ctx.textAlign = "center";
                ctx.font = "24px Arial";
                ctx.fillStyle = "white";
                ctx.fillText(gameOver ? "ðŸ˜¥ GAME OVER" : "ðŸŽ‰ MISSION COMPLETE!", centerX, centerY - 20);
                ctx.font = "18px Arial";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 10);
                ctx.fillText("ðŸ”„", centerX, centerY + 40);
                return; // draw ì¤‘ë‹¨
            }

            // ë¸Œë¦­ê±¸ ìŠ¤í”„ë¼ì´íŠ¸ ê·¸ë¦¬ê¸°
            // ctx.font = "20px Arial";
            // ctx.fillText("ðŸ§±", girlX, girlYRef.current);

