            const drawWidth = frameWidth * scale;    // 102.3
            const drawHeight = frameHeight * scale;  // 153.6

            ctx.drawImage(
                spriteRef.current,
                frameX.current * frameWidth,
                frameY.current * frameHeight,
                frameWidth,
                frameHeight,
                girlX,
                girlYRef.current,
                drawWidth,
                drawHeight
            );

            // í”„ë ˆìž„ ì „í™˜
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
                if (frameX.current === 0) frameY.current = (frameY.current + 1) % numRows;
            }
            frameCount.current++;

            // ë¸Œë¦­ê±¸ ì´ë™
            if (keys.current.up && girlYRef.current > 20) girlYRef.current -= girlSpeed;
            if (keys.current.down && girlYRef.current < canvas.height - 20) girlYRef.current += girlSpeed;

            // ë²½ëŒ ì´ë™ ë° ì  ì¶©ëŒ ì²´í¬
            ctx.font = "16px Arial";
            bricksRef.current.forEach((brick, i) => {
                brick.x += brick.speed;
                ctx.fillText("ðŸ§±", brick.x, brick.y);

                enemiesRef.current.forEach((enemy, j) => {
                    if (
                        brick.x > enemy.x - 10 &&
                        brick.x < enemy.x + 10 &&
                        brick.y > enemy.y - 10 &&
                        brick.y < enemy.y + 10
                    ) {
                        bricksRef.current.splice(i, 1);
                        enemiesRef.current.splice(j, 1);
                        scoreRef.current++;
                        setScore(scoreRef.current);
                        if (scoreRef.current >= 50) setGameClear(true);
                    }
                });

                if (brick.x > canvas.width) bricksRef.current.splice(i, 1);
            });

            // ì  ì´ë™ ë° ì¶©ëŒ
            ctx.font = "20px Arial";
            ctx.fillStyle = "#f472b6";
            enemiesRef.current.forEach((enemy, i) => {
                enemy.x -= enemy.speed;
                ctx.fillText(enemy.emoji, enemy.x, enemy.y);

                if (
                    enemy.x < girlX + 10 &&
                    enemy.x > girlX - 10 &&
                    enemy.y > girlYRef.current - 10 &&
                    enemy.y < girlYRef.current + 10
                ) setGameOver(true);

                if (enemy.x < -30) {
                    enemiesRef.current.splice(i, 1);
                    scoreRef.current = Math.max(0, scoreRef.current - 1);
                    setScore(scoreRef.current);
                }
