import React, { useRef, useEffect, useState } from 'react';

const BrickGirlRT = () => {
    const canvasRef = useRef(null);
    const spriteRef = useRef(new Image());

    // 스프라이트 설정
    const frameWidth = 335;
    const frameHeight = 512;
    const numCols = 3;
    const numRows = 2;
    const scale = 0.2;

    const frameX = useRef(0);
    const frameY = useRef(0); // 0: 걷기, 1: 점프 시로 변경 예정
    const frameCount = useRef(0);
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // 캐릭터 위치 및 점프 관련 변수
    let girlX = 50;
    let girlY = 300;
    let isJumping = false;
    let velocityY = 0;
    const gravity = 0.8;
    let jumpCount = 0; // 현재 점프 횟수 (0~2)
    const maxJumps = 2;

    // 벽돌 배열 추가
    const bricks = useRef([]);

    // 지형(타일) 데이터 배열 만들기
    const scrollSpeed = 2; // 배경 스크롤 속도
    const tileSize = 50;
    const groundTiles = useRef([]);
    const levelData = [
        { x: 0, y: 350 },   // 땅
        { x: 50, y: 350 },
        { x: 100, y: 350 },
        { x: 150, y: 350 },
        { x: 200, y: 350 },
        // 간격
        { x: 300, y: 350 },
        { x: 350, y: 350 },
        { x: 400, y: 350 },
        // 높은 지형
        { x: 500, y: 300 },
        { x: 550, y: 300 },
        { x: 600, y: 250 },
        { x: 650, y: 250 },
        { x: 700, y: 200 },
        { x: 750, y: 200 },
    ];


    // 초기 타일 30개 정도 생성
    useEffect(() => {
        const tiles = [];
        for (let i = 0; i < 30; i++) {
            tiles.push({ x: i * tileSize, y: 350, type: 'ground' });
        }
        groundTiles.current = levelData.map((tile) => ({
            ...tile,
            type: 'ground',
        }));
    }, []);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let animationFrameId;

        // 캔버스 사이즈 설정
        canvas.width = 800;
        canvas.height = 400;

        // 스프라이트 이미지 로드
        spriteRef.current.src = process.env.PUBLIC_URL + '/brickgirl_sprite.png';
        spriteRef.current.onload = () => {
            setSpriteLoaded(true);
        };

        // 키보드 이벤트 처리
        const handleKeyDown = (e) => {
            if (e.code === 'Space') {
                e.preventDefault(); // 스페이스바 스크롤 방지
                throwBrick();
            } else if (e.code === 'ArrowUp') {
                if (jumpCount < maxJumps) {
                    velocityY = -12 * (jumpCount === 1 ? 1.2 : 1); // 2단 점프는 더 높이
                    isJumping = true;
                    jumpCount++;
                }
            }
        };

        // 모바일 터치 조작
        const handleTouchStart = (e) => {
            const touchX = e.touches[0].clientX;

            if (touchX < canvas.width / 2) {
                // 왼쪽 터치 → 점프
                if (!isJumping) {
                    isJumping = true;
                    velocityY = -12;
                }
            } else {
                // 오른쪽 터치 → 공격
                throwBrick();
            }
        };

        // 애니메이션 프레임 업데이트
        const updateFrame = () => {
            frameCount.current++;
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
            }
        };

        // 그리기
        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // --- 바닥 타일 스크롤 ---
            groundTiles.current.forEach((tile) => {
                tile.x -= scrollSpeed; // ← 왼쪽으로 이동
                ctx.fillStyle = 'saddlebrown'; // 타일 색상
                ctx.fillRect(tile.x, tile.y, tileSize, tileSize);
            });

            const lastTile = groundTiles.current[groundTiles.current.length - 1];
            if (lastTile.x < canvas.width) {
                groundTiles.current.push({
                    x: lastTile.x + tileSize,
                    y: 350,
                    type: 'ground',
                });
            }

            // 중력 적용 및 점프 처리
            if (isJumping) {
                velocityY += gravity;
                girlY += velocityY;

                if (girlY >= 300) {
                    girlY = 300;
                    isJumping = false;
                    velocityY = 0;
                    jumpCount = 0; // ✅ 착지 시 점프 횟수 초기화
                }
            }

            // 스프라이트 그리기
            if (spriteLoaded) {
                updateFrame();
                ctx.drawImage(
                    spriteRef.current,
                    frameX.current * frameWidth,
                    frameY.current * frameHeight,
                    frameWidth,
                    frameHeight,
                    girlX,
                    girlY,
                    frameWidth * scale,
                    frameHeight * scale
                );
            } else {
                // 대체 이미지 (로딩 전)
                ctx.fillStyle = 'hotpink';
                ctx.fillRect(girlX, girlY, 30, 30);
            }

            // 벽돌 그리기 + 이동
            ctx.font = "16px Arial";
            bricks.current.forEach((brick, index) => {
                // 위치 업데이트 (포물선)
                brick.x += brick.vx;
                brick.vy += gravity * 0.5; // 중력 영향
                brick.y += brick.vy;

                // 벽돌 그리기
                ctx.fillText('🧱', brick.x, brick.y);

                // 화면 밖으로 나가면 제거
                if (brick.x > canvas.width || brick.y > canvas.height) {
                    bricks.current.splice(index, 1);
                }
            });


            animationFrameId = requestAnimationFrame(draw);
        };

        // 초기 설정
        window.addEventListener('keydown', handleKeyDown);
        canvas.addEventListener('touchstart', handleTouchStart);
        draw();

        // 정리 함수
        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            canvas.removeEventListener('touchstart', handleTouchStart);
            cancelAnimationFrame(animationFrameId);
        };
    }, [spriteLoaded]);

    // 벽돌 던지기 함수
    const throwBrick = () => {
        // 브릭걸 손에서 날아가는 초기 위치
        const startX = girlX + 30;
        const startY = girlY + 10;

        // 초기 속도 설정 (포물선)
        const vx = 6 + Math.random(); // 속도 조금 랜덤하게
        const vy = -8; // 위로 튀어나가는 각도

        // 벽돌 하나 추가
        bricks.current.push({ x: startX, y: startY, vx, vy });
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <h2>Brick Girl: Run & Throw</h2>
            <canvas ref={canvasRef} style={{ border: '2px solid #999' }} />
            <p>⬆️: Jump / Space: 🧱 (모바일은 화면 좌우 터치)</p>
        </div>
    );
};

export default BrickGirlRT;
