import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const mindMapData = {
  label: "ME",
  color: "bg-lime-500",
  children: [
    {
      label: "Tech Stack",
      color: "bg-blue-400",
      children: [
        { label: "Java", color: "bg-blue-300" },
        { label: "JavaScript", color: "bg-blue-300" },
        { label: "HTML/CSS", color: "bg-blue-200" },
        { label: "Python", color: "bg-blue-200" },
        { label: "C#", color: "bg-blue-200" },
        { label: "Vue.js", color: "bg-blue-100" },
        { label: "Django", color: "bg-blue-100" }
      ]
    },
    {
      label: "Hobbies",
      color: "bg-orange-400",
      children: [
        {
          label: "Game",
          color: "bg-orange-300",
          children: [
            { label: "Diablo", color: "bg-orange-200" },
            { label: "Animal Crossing", color: "bg-orange-100" },
            { label: "Mine Craft", color: "bg-orange-100" }
          ]
        },
        {
          label: "Writing", color: "bg-orange-300",
          children: [
            { label: "Essay", color: "bg-orange-200" },
            { label: "Novel", color: "bg-orange-100" }
          ]
        },
        {
          label: "Listening Music", color: "bg-orange-300",
          children: [
            { label: "Classical Music", color: "bg-orange-200" },
            { label: "Jazz", color: "bg-orange-200" },
            { label: "K-pop", color: "bg-orange-100" }
          ]
        },
        { label: "Piano", color: "bg-orange-200" },
        { label: "Game Making", color: "bg-orange-100" },
        { label: "Caligraphy", color: "bg-orange-100" }
      ]
    },
    {
      label: "Languages",
      color: "bg-purple-700",
      children: [
        { label: "Korean ⭐⭐⭐", color: "bg-purple-600" },
        { label: "English ⭐⭐", color: "bg-purple-500" },
        { label: "Chinese ⭐⭐", color: "bg-purple-400" },
        { label: "Japanese ⭐", color: "bg-purple-300" }
      ]
    }
  ]
};

export default function MindMapPage() {
  const [path, setPath] = useState([]);
  const currentNode = path[path.length - 1];
  const isRoot = path.length === 0;

  const handleClick = (node) => {
    if (node.children) {
      setPath([...path, node]);
    }
  };

  const handleBack = () => {
    if (path.length > 0) {
      setPath(path.slice(0, -1));
    }
  };

  const baseCircleClass =
    "absolute rounded-full text-white text-center flex items-center justify-center shadow-xl border-4 border-white/20 dark:border-white/10 transition-all duration-300 bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-white/10 to-black/10";

  const getRadialLayout = (count, radius) => {
    const layerCounts = [3, 4, 5];
    let layout = [];
    let index = 0;
    let angleOffset = -90;

    for (let layer = 0; layerCounts[layer] && index < count; layer++) {
      const itemsInLayer = layerCounts[layer];
      const step = 360 / itemsInLayer;
      for (let i = 0; i < itemsInLayer && index < count; i++, index++) {
        const angle = angleOffset + step * i;
        const x = radius * (layer + 1) * Math.cos((angle * Math.PI) / 180);
        const y = radius * (layer + 1) * Math.sin((angle * Math.PI) / 180);
        layout.push({ x, y });
      }
    }
    return layout;
  };

  return (
    <div className="relative w-screen h-screen bg-white dark:bg-gray-900 overflow-hidden">
      {/* Back button */}
      {path.length > 0 && (
        <button
          onClick={handleBack}
          className="absolute top-4 left-4 text-sm bg-gray-700 px-3 py-1 rounded-full text-white z-20"
        >
          Back
        </button>
      )}

      {/* Current Node (fixed top-left corner) */}
      <AnimatePresence>
        {currentNode && (
          <motion.div
            key={currentNode.label}
            initial={{ left: "50%", top: "50%", translateX: "-50%", translateY: "-50%", opacity: 0 }}
            animate={{ left: 80, top: 80, translateX: 0, translateY: 0, opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.5 }}
            className={`w-28 h-28 ${currentNode.color} font-bold text-xl z-10 ${baseCircleClass}`}
          >
            {currentNode.label}
          </motion.div>
        )}
      </AnimatePresence>

      {/* Children Nodes in Radial Layout */}
      {currentNode?.children && (
        <AnimatePresence>
          {getRadialLayout(currentNode.children.length, 100).map((pos, i) => {
            const child = currentNode.children[i];
            return (
              <motion.button
                key={child.label}
                initial={{ opacity: 0, scale: 0 }}
                animate={{
                  opacity: 1,
                  scale: 1,
                  x: pos.x + 80,
                  y: pos.y + 80
                }}
                exit={{ opacity: 0, scale: 0 }}
                transition={{ duration: 0.5, delay: i * 0.05 }}
                className={`w-20 h-20 ${child.color} font-semibold z-0 ${baseCircleClass}`}
                onClick={() => handleClick(child)}
              >
                {child.label}
              </motion.button>
            );
          })}
        </AnimatePresence>
      )}

      {/* Root Node Center */}
      {isRoot && (
        <motion.button
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          className={`absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-28 h-28 ${mindMapData.color} font-bold text-xl ${baseCircleClass}`}
          onClick={() => handleClick(mindMapData)}
        >
          {mindMapData.label}
        </motion.button>
      )}
    </div>
  );
}
