import React, { useState } from "react";

const mindMapData = {
  label: "ME",
  color: "bg-lime-500",
  children: [
    {
      label: "Tech Stack",
      color: "bg-blue-400",
      children: [
        { label: "Java", color: "bg-blue-300" },
        { label: "Python", color: "bg-yellow-400" },
        { label: "Vue.js", color: "bg-green-400" },
        { label: "Django", color: "bg-emerald-400" }
      ]
    },
    {
      label: "Hobbies",
      color: "bg-orange-400",
      children: [
        { label: "Writing", color: "bg-pink-400" },
        { label: "Piano", color: "bg-red-300" },
        { label: "Game Making", color: "bg-violet-400" }
      ]
    },
    {
      label: "Languages",
      color: "bg-purple-700",
      children: [
        { label: "Korean", color: "bg-purple-500" },
        { label: "English", color: "bg-indigo-400" },
        { label: "Chinese", color: "bg-yellow-300" },
        { label: "Japanese", color: "bg-red-400" }
      ]
    }
  ]
};

export default function MindMapPage() {
  const [depth, setDepth] = useState(0);
  const [selectedNode, setSelectedNode] = useState(null);
  const [previousNode, setPreviousNode] = useState(null);

  const handleClick = (node) => {
    if (depth === 0) {
      setDepth(1);
      setSelectedNode(node);
    } else if (depth === 1) {
      setDepth(2);
      setPreviousNode(selectedNode);
      setSelectedNode(node);
    }
  };

  const handleBack = () => {
    if (depth === 2 && previousNode) {
      setDepth(1);
      setSelectedNode(previousNode);
      setPreviousNode(null);
    } else if (depth === 1) {
      setDepth(0);
      setSelectedNode(null);
    }
  };

  const renderNodes = () => {
    if (depth === 0) {
      return [
        <button
          key="me"
          className={`w-28 h-28 rounded-full ${mindMapData.color} text-white font-bold text-center flex items-center justify-center shadow-md transition-all`}
          onClick={() => handleClick(mindMapData)}
        >
          {mindMapData.label}
        </button>
      ];
    }

    if ((depth === 1 || depth === 2) && selectedNode) {
      return [
        <button
          key="back-btn"
          onClick={handleBack}
          className={`w-16 h-16 rounded-full ${selectedNode.color} text-white font-bold text-center flex items-center justify-center shadow-md mb-6 hover:opacity-80 transition`}
        >
          {selectedNode.label}
        </button>,
        ...(selectedNode.children?.map((child, i) => (
          <button
            key={child.label}
            className={`w-24 h-24 rounded-full ${child.color} text-white font-semibold text-center flex items-center justify-center shadow-md transition-all`}
            onClick={() => handleClick(child)}
          >
            {child.label}
          </button>
        )) || [])
      ];
    }

    return null;
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center space-y-6 p-8">
      {renderNodes()}
    </div>
  );
}
