import { useEffect, useRef, useState } from "react";

export default function Game() {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

    // Î∏åÎ¶≠Í±∏ ÏÉÅÌÉú
    const girlX = 50;
    const girlYRef = useRef(0); // Ï†êÌîÑ ÏúÑÏπòÏö©
    const isJumping = useRef(false);
    const velocity = useRef(0);

    // ÏÑ†Ïù∏Ïû•
    const cactusXRef = useRef(canvasSize.width);
    const cactusY = canvasSize.height - 30;

    // Í≤åÏûÑ Î£®ÌîÑ
    const animationRef = useRef();

    // Game over
    const [gameOver, setGameOver] = useState(false);

    // Ï†êÏàò
    const [score, setScore] = useState(0);
    const scoreRef = useRef(0);
    const cactusPassed = useRef(false);

    // Î∏åÎ¶≠ ÏÉÅÌÉú
    const bricksRef = useRef([]);

    // Î≤ΩÎèå Î∞úÏÇ¨ Ìï®Ïàò
    const shootBrick = () => {
        bricksRef.current.push({
            x: girlX + 10,
            y: girlYRef.current,
            speed: 7,
        });
    };

    // ÌÇ§ or ÌÑ∞Ïπò Ïù¥Î≤§Ìä∏ Ïó∞Í≤∞
    const handleAction = (e) => {
        if (e.type === "keydown") {
            if (e.key === " " || e.key === "Spacebar") shootBrick(); // Ïä§ÌéòÏù¥Ïä§Î∞î
            else if (!isJumping.current) {
                isJumping.current = true;
                velocity.current = -15;
            }
        } else {
            shootBrick(); // ÌÑ∞Ïπò Ïãú ÎçòÏßÄÍ∏∞
        }
    };

    window.addEventListener("keydown", handleAction);
    canvas.addEventListener("touchstart", handleAction);


    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        girlYRef.current = canvasSize.height - 40;

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Î∞∞Í≤Ω
            ctx.fillStyle = "#1e293b"; // Ïñ¥ÎëêÏö¥ Î∞§ Î∞∞Í≤Ω
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            // Î∏åÎ¶≠Í±∏
            ctx.font = "24px Arial";
            ctx.fillText("üß±", girlX, girlYRef.current);

            // ÏÑ†Ïù∏Ïû•
            ctx.font = "20px Arial";
            ctx.fillText("üåµ", cactusXRef.current, cactusY);

            // Ï†êÌîÑ Î°úÏßÅ
            if (isJumping.current) {
                girlYRef.current += velocity.current;
                velocity.current += 1; // gravity

                if (girlYRef.current >= canvasSize.height - 40) {
                    girlYRef.current = canvasSize.height - 40;
                    isJumping.current = false;
                    velocity.current = 0;
                }
            }

            // ÏÑ†Ïù∏Ïû• Ïù¥Îèô
            cactusXRef.current -= 5;

            // Î≤ΩÎèå Ïù¥Îèô & Í∑∏Î¶¨Í∏∞
            ctx.fillStyle = "#f87171"; // Îπ®Í∞Ñ Î≤ΩÎèå
            bricksRef.current.forEach((brick, index) => {
                brick.x += brick.speed;
                ctx.font = "16px Arial";
                ctx.fillText("üß±", brick.x, brick.y);

                // üåµÏóê ÎßûÏïòÎäîÏßÄ ÌôïÏù∏
                const cactusX = cactusXRef.current;
                const cactusYTop = cactusY - 20;
                if (
                    brick.x > cactusX - 10 &&
                    brick.x < cactusX + 10 &&
                    brick.y > cactusYTop &&
                    brick.y < cactusY + 20
                ) {
                    // ÏÑ†Ïù∏Ïû• Ï†úÍ±∞ + Ï†êÏàò Ï¶ùÍ∞Ä
                    cactusXRef.current = canvas.width + Math.random() * 200;
                    bricksRef.current.splice(index, 1);
                    cactusPassed.current = false;
                    scoreRef.current += 1;
                    setScore(scoreRef.current);
                }

                // Î≤ΩÎèåÏù¥ ÌôîÎ©¥ÏùÑ Î≤óÏñ¥ÎÇòÎ©¥ Ï†úÍ±∞
                if (brick.x > canvas.width + 20) {
                    bricksRef.current.splice(index, 1);
                }
            });
            // Game over
            if (
                cactusXRef.current < girlX + 20 &&
                cactusXRef.current > girlX - 20 &&
                girlYRef.current >= canvasSize.height - 50
            ) {
                setGameOver(true);
                cancelAnimationFrame(animationRef.current);
                ctx.fillStyle = "white";
                ctx.font = "24px Arial";
                ctx.fillText("üò¢ GAME OVER", canvasSize.width / 2, canvasSize.height / 2);
                return;
            }

            // Ï†êÏàò Ï°∞Í±¥
            if (!cactusPassed.current && cactusXRef.current + 20 < girlX) {
                cactusPassed.current = true;
                scoreRef.current += 1;
                setScore(scoreRef.current);
            }

            // ÏÑ†Ïù∏Ïû• Î¶¨ÏÖã
            if (cactusXRef.current + 20 < 0) {
                cactusXRef.current = canvasSize.width + Math.random() * 200;
                cactusPassed.current = false; // Ï†êÏàò Ï°∞Í±¥ Ï¥àÍ∏∞Ìôî
            }

            // Score
            ctx.fillStyle = "#fbbf24";
            ctx.font = "16px Arial";
            ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);

            animationRef.current = requestAnimationFrame(draw);
        };


        draw();

        const handleJump = () => {
            if (!isJumping.current) {
                isJumping.current = true;
                velocity.current = -15;
            }
        };

        window.addEventListener("keydown", handleJump);
        canvas.addEventListener("touchstart", handleJump);

        return () => {
            window.removeEventListener("keydown", handleJump);
            canvas.removeEventListener("touchstart", handleJump);
            cancelAnimationFrame(animationRef.current);
        };
    }, [canvasSize]);

    // Î∞òÏùëÌòï
    useEffect(() => {
        const updateSize = () => {
            const width = Math.min(window.innerWidth - 10, 480);
            const height = Math.round(width * 0.75);
            setCanvasSize({ width, height });
        };
        updateSize();
        window.addEventListener("resize", updateSize);
        return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
        <div className="flex flex-col items-center mt-6">
            <h2 className="text-2xl font-bold mb-4">üß± Brick Girl Run</h2>
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                className="bg-black rounded shadow-md"
                style={{ width: "100%", maxWidth: "480px" }}
            />
        </div>
    );
}
