import React, { useState } from "react";

const mindMapData = {
  label: "ME",
  color: "bg-lime-500",
  children: [
    {
      label: "Tech Stack",
      color: "bg-blue-400",
      children: [
        { label: "Java", color: "bg-blue-300" },
        { label: "Python", color: "bg-yellow-400" },
        { label: "Vue.js", color: "bg-green-400" },
        { label: "Django", color: "bg-emerald-400" }
      ]
    },
    {
      label: "Hobbies",
      color: "bg-orange-400",
      children: [
        { label: "Writing", color: "bg-pink-400" },
        { label: "Piano", color: "bg-red-300" },
        { label: "Game Making", color: "bg-violet-400" }
      ]
    },
    {
      label: "Languages",
      color: "bg-purple-700",
      children: [
        { label: "Korean", color: "bg-purple-500" },
        { label: "English", color: "bg-indigo-400" },
        { label: "Chinese", color: "bg-yellow-300" },
        { label: "Japanese", color: "bg-red-400" }
      ]
    }
  ]
};

export default function MindMapPage() {
  const [depth, setDepth] = useState(0);
  const [selectedNode, setSelectedNode] = useState(null);
  const [previousNode, setPreviousNode] = useState(null);

  const handleClick = (node) => {
    if (depth === 0) {
      setDepth(1);
      setSelectedNode(node);
    } else if (depth === 1) {
      setDepth(2);
      setPreviousNode(selectedNode);
      setSelectedNode(node);
    }
  };

  const handleBack = () => {
    if (depth === 2 && previousNode) {
      setDepth(1);
      setSelectedNode(previousNode);
      setPreviousNode(null);
    } else if (depth === 1) {
      setDepth(0);
      setSelectedNode(null);
    }
  };

  const ripple = (e) => {
    const button = e.currentTarget;
    const ripple = document.createElement("span");
    ripple.className = "absolute rounded-full bg-white/40 animate-ping w-10 h-10";
    ripple.style.left = `${e.nativeEvent.offsetX - 20}px`;
    ripple.style.top = `${e.nativeEvent.offsetY - 20}px`;
    button.appendChild(ripple);
    setTimeout(() => ripple.remove(), 500);
  };

  const baseCircleClass =
    "rounded-full text-white text-center flex items-center justify-center shadow-xl border-4 border-white/20 dark:border-white/10 transition-all duration-300 bg-[radial-gradient(ellipse_at_top_left,_var(--tw-gradient-stops))] from-white/10 to-black/10";
  const hoverClass = "hover:scale-110 hover:brightness-110";

  const renderNodes = () => {
    if (depth === 0) {
      return [
        <button
          key="me"
          className={`w-28 h-28 ${mindMapData.color} font-bold text-xl ${baseCircleClass} ${hoverClass} relative overflow-hidden`}
          onClick={(e) => {
            ripple(e);
            handleClick(mindMapData);
          }}
        >
          {mindMapData.label}
        </button>
      ];
    }

    if ((depth === 1 || depth === 2) && selectedNode) {
      return [
        <button
          key="back-btn"
          onClick={handleBack}
          className={`w-16 h-16 ${selectedNode.color} font-bold text-sm ${baseCircleClass} ${hoverClass} mb-4 mt-2`}
        >
          {selectedNode.label}
        </button>,
        ...(selectedNode.children?.map((child, i) => (
          <button
            key={child.label}
            className={`w-24 h-24 ${child.color} font-semibold ${baseCircleClass} ${depth === 1 ? hoverClass : ""} relative overflow-hidden`}
            onClick={(e) => {
              if (depth < 2) {
                ripple(e);
                handleClick(child);
              }
            }}
          >
            {child.label}
          </button>
        )) || [])
      ];
    }

    return null;
  };

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-white flex flex-col items-center justify-start space-y-6 pt-20 pb-8 px-4">
      {renderNodes()}
    </div>
  );
}
