const projects = [
    {
      title: "B2B Data Management System",
      description: "기업용 데이터 분석/관리 플랫폼. 실시간 매출 시각화 및 Excel 업로드 기능 포함.",
      role: "Full-Stack Developer (Solo)",
      tech: ["Python", "Django", "MySQL", "AWS", "Chart.js"],
      link: null
    },
    {
      title: "Simple Dashboard",
      description: "OAuth 로그인, 사용자 통계 대시보드, 이메일 인증 및 세션 추적 기능 포함.",
      role: "Full-Stack Developer (Solo)",
      tech: ["Node.js", "JavaScript", "MySQL", "OAuth"],
      link: "https://github.com/PatriciaPark/simple-dashboard"
    },
    {
      title: "Astronomy Web & 3D Simulation",
      description: "연구용 웹사이트 및 우주 확장 시뮬레이션. 연구 데이터 시각화 및 인터랙티브 모델 포함.",
      role: "Full-Stack Developer (Solo)",
      tech: ["Java", "JavaScript", "Unity3D", "MATLAB", "Blender"],
      link: "https://drive.google.com/file/d/1JIsROXUPEJb28KuV8tae9m8HHLBCaYfb/view"
    },
    {
      title: "Lotte Mart M-Coupon Payment API",
      description: "결제 시스템 개선 및 안정화. Spring 기반 API 설계 및 성능 최적화.",
      role: "Freelance Full-Stack Developer",
      tech: ["Java", "Spring", "Oracle"],
      link: null
    },
    {
      title: "Dokdo 3D Content Project",
      description: "3D 콘텐츠 및 투영 페인팅 구현. KT 5G 프로젝트 및 MVC 2018 전시.",
      role: "3D Developer",
      tech: ["Unity3D", "C#", "Photoshop", "PostgreSQL"],
      link: null
    }
  ];
  
  export default function Projects() {
    return (
      <main className="min-h-screen bg-white text-gray-800 dark:bg-gray-900 dark:text-gray-100 p-6">
        <h1 className="text-4xl font-bold text-center mb-10">Projects</h1>
        <div className="grid md:grid-cols-2 gap-8 max-w-6xl mx-auto">
          {projects.map((project, index) => (
            <div key={index} className="border rounded-2xl shadow-md p-6 hover:shadow-lg transition">
              <h2 className="text-2xl font-semibold mb-2">{project.title}</h2>
              <p className="mb-2 text-gray-600">{project.description}</p>
              <p className="mb-1 text-sm text-gray-500 italic">{project.role}</p>
              <p className="mb-2 text-sm text-gray-700">Tech Stack: {project.tech.join(", ")}</p>
              {project.link && (
                <a href={project.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 underline text-sm">
                  View More
                </a>
              )}
            </div>
          ))}
        </div>
      </main>
    );
  }
