import { useEffect, useRef, useState } from "react";

export default function Game() {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

    // Î∏åÎ¶≠Í±∏ ÏúÑÏπò Î∞è Ïù¥Îèô Í¥ÄÎ†®
    const girlX = 30;
    const girlYRef = useRef(150);
    const girlSpeed = 4;
    const keys = useRef({ up: false, down: false });

    // Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïï†ÎãàÎ©îÏù¥ÏÖò Í¥ÄÎ†® ÏÑ§Ï†ï
    const spriteRef = useRef(new Image());
    const frameWidth = 335;   // Math.floor(1024 / 3) = 341
    const frameHeight = 512;  // Math.floor(1024 / 2) = 512
    const numCols = 3;  // Ïù¥ÎØ∏ÏßÄ Í∞ÄÎ°ú ÌîÑÎ†àÏûÑ Ïàò
    const numRows = 2;  // Ïù¥ÎØ∏ÏßÄ ÏÑ∏Î°ú ÌîÑÎ†àÏûÑ Ïàò
    const frameX = useRef(0);
    const frameY = useRef(0);
    const frameCount = useRef(0);
    const scale = 0.1;  // Î∏åÎ¶≠Í±∏ Ï∫êÎ¶≠ÌÑ∞ ÏÇ¨Ïù¥Ï¶à
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // Ï†Å Ïö∞Ï£ºÏÑ† ÏÉÅÌÉú
    const enemiesRef = useRef([]);

    // Î≤ΩÎèå Í¥ÄÎ†®
    const bricksRef = useRef([]);
    const fireBricksRef = useRef(() => { });
    const shootCooldownRef = useRef(false);
    const shootIntervalRef = useRef(null);

    // Ìä∏Î¶¨ÌîåÏÉ∑ ÏÉÅÌÉú
    const tripleShotTimerRef = useRef(null);
    const [tripleShot, setTripleShot] = useState(false);

    // ÏïÑÏù¥ÌÖú ÏÉÅÌÉú
    const itemRef = useRef(null);

    // Ïï†ÎãàÎ©îÏù¥ÏÖò Î£®ÌîÑ
    const animationRef = useRef();
    const isTouching = useRef(false);

    // Î≥ÑÎπõ Î∞∞Í≤Ω
    const starsRef = useRef([]);

    // Ï†êÏàò ÏÉÅÌÉú
    const scoreRef = useRef(0);
    const [score, setScore] = useState(0);

    // Í≤åÏûÑ ÏÉÅÌÉú
    const [gameOver, setGameOver] = useState(false);
    const [gameClear, setGameClear] = useState(false);

    // Î≥ÑÎπõ Ï¥àÍ∏∞Ìôî
    useEffect(() => {
        const stars = Array.from({ length: 100 }, () => ({
            x: Math.random() * canvasSize.width,
            y: Math.random() * canvasSize.height,
            size: Math.random() * 0.8 + 0.2,
            speed: Math.random() * 1 + 0.5,
        }));
        starsRef.current = stars;
    }, [canvasSize]);

    // Î∏åÎ¶≠Í±∏ Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïù¥ÎØ∏ÏßÄ Î°úÎìú
    useEffect(() => {
        spriteRef.current.src = process.env.PUBLIC_URL + "/brickgirl_sprite.png";
        spriteRef.current.onload = () => setSpriteLoaded(true);
    }, []);

    // 1. tripleShot ÏÉÅÌÉúÍ∞Ä Î∞îÎÄåÎ©¥ shootIntervalÏùÑ Ïû¨ÏãúÏûë
    useEffect(() => {
        if (isTouching.current) {
            // Ïù¥ÎØ∏ Ïù∏ÌÑ∞Î≤åÏù¥ ÏûàÎã§Î©¥ Ï†úÍ±∞ÌïòÍ≥† Ïû¨ÏÑ§Ï†ï
            if (shootIntervalRef.current) {
                clearInterval(shootIntervalRef.current);
            }
            shootIntervalRef.current = setInterval(() => {
                fireBricksRef.current();
            }, 250);
        }

        return () => clearInterval(shootIntervalRef.current);
    }, [tripleShot]);

    // fireBricks Ìï®Ïàò ÏµúÏã† ÏÉÅÌÉú Î∞òÏòÅ
    useEffect(() => {
        fireBricksRef.current = () => {
            const baseY = girlYRef.current;
            if (tripleShot) {
                bricksRef.current.push({ x: girlX + 10, y: baseY + 50, speedX: 6, speedY: 0 });
                bricksRef.current.push({ x: girlX + 10, y: baseY + 30, speedX: 6, speedY: 0 });
                bricksRef.current.push({ x: girlX + 10, y: baseY + 10, speedX: 6, speedY: 0 });
            } else {
                bricksRef.current.push({ x: girlX + 10, y: baseY + 30, speedX: 6, speedY: 0 });
            }
        };
    }, [tripleShot]);

    // Í≤åÏûÑ main Î£®ÌîÑ Ïã§Ìñâ
    useEffect(() => {
        if (!spriteLoaded) return;  // Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏïÑÏßÅ Î°úÎìúÎêòÏßÄ ÏïäÏúºÎ©¥ Ïã§Ìñâ X

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "black";
            ctx.fillRect(0, 0, canvas.width, canvas.height);

            // Î∞∞Í≤Ω Î≥ÑÎπõ
            ctx.fillStyle = "white";
            starsRef.current.forEach((star) => {
                star.x -= star.speed;
                if (star.x < 0) star.x = canvas.width;
                ctx.beginPath();
                ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
                ctx.fill();
            });

            // Í≤åÏûÑ Ï¢ÖÎ£å Ï≤òÎ¶¨
            if (gameOver || gameClear) {
                // GA Ï∂îÏ†Å
                gtag("event", "game_over", {
                    event_category: "game",
                    event_label: "brickgirl",
                });

                const centerX = canvas.width / 2;
                const centerY = canvas.height / 2;
                ctx.font = "16px 'Press Start 2P'";
                ctx.textAlign = "center";
                ctx.fillStyle = "white";
                ctx.fillText(gameOver ? "üò• GAME OVER" : "üéâ MISSION COMPLETE!", centerX, centerY - 20);
                ctx.font = "13px 'Press Start 2P'";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 20);
                return; // draw Ï§ëÎã®
            }

            // Î∏åÎ¶≠Í±∏ Ïä§ÌîÑÎùºÏù¥Ìä∏ Í∑∏Î¶¨Í∏∞
            ctx.drawImage(
                spriteRef.current,
                frameX.current * frameWidth,
                frameY.current * frameHeight,
                frameWidth,
                frameHeight,
                girlX,
                girlYRef.current,
                frameWidth * scale,
                frameHeight * scale
            );

            // ÌîÑÎ†àÏûÑ Ï†ÑÌôò Ïï†ÎãàÎ©îÏù¥ÏÖò
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
                if (frameX.current === 0) frameY.current = (frameY.current + 1) % numRows;
            }
            frameCount.current++;

            // Î∏åÎ¶≠Í±∏ Ïù¥Îèô
            if (keys.current.up && girlYRef.current > -5) girlYRef.current -= girlSpeed;    // 20 Ïà´ÏûêÎ•º Ï§ÑÏù¥Î©¥ ÏúÑÎ°ú Îçî Ïù¥Îèô Í∞ÄÎä•(10)
            if (keys.current.down && girlYRef.current < canvas.height - 50) girlYRef.current += girlSpeed;  // -20 Ïà´ÏûêÎ•º Ïò¨Î¶¨Î©¥ ÏúÑÎ°ú Ïù¥Îèô(-50)

            // Î≤ΩÎèå Í∑∏Î¶¨Í∏∞
            ctx.font = "16px Arial";
            bricksRef.current.forEach((b, i) => {
                b.x += b.speedX;
                b.y += b.speedY;
                ctx.fillText("üß±", b.x, b.y);

                // Ï†ÅÍ≥º Ï∂©Îèå ÌôïÏù∏
                enemiesRef.current.forEach((e, j) => {
                    const dx = b.x - e.x, dy = b.y - e.y, dist = Math.sqrt(dx * dx + dy * dy);
                    if (dist < 20) {
                        enemiesRef.current.splice(j, 1);
                        bricksRef.current.splice(i, 1);
                        scoreRef.current++;
                        setScore(scoreRef.current);
                        if (scoreRef.current >= 50) setGameClear(true);
                    }
                });
            });

            // Ï†Å Ïù¥Îèô Î∞è Ï∂©Îèå
            ctx.fillStyle = "#f472b6";
            ctx.font = "20px Arial";

            enemiesRef.current.forEach((e, i) => {
                e.x -= e.speed;
                ctx.fillText(e.emoji, e.x, e.y);

                // Î∏åÎ¶≠Í±∏Ïùò hitbox (Í∞ÄÎ°ú, ÏÑ∏Î°ú)
                const girlTop = girlYRef.current;
                const girlBottom = girlYRef.current + frameHeight * 0.06;
                const girlLeft = girlX;
                const girlRight = girlX + frameWidth * 0.07;

                // Ï†Å(enemy)Ïùò hitbox (Ï£ºÎ°ú ¬±10)
                const enemyTop = e.y - 5;
                const enemyBottom = e.y + 5;
                const enemyLeft = e.x - 5;
                const enemyRight = e.x + 5;

                // AABB Î∞©Ïãù Ï∂©Îèå ÌåêÏ†ï
                const isColliding =
                    girlRight > enemyLeft &&
                    girlLeft < enemyRight &&
                    girlBottom > enemyTop &&
                    girlTop < enemyBottom;

                if (isColliding)
                    setGameOver(true);
                if (e.x < -30) {
                    enemiesRef.current.splice(i, 1);
                    scoreRef.current = Math.max(0, scoreRef.current - 1);
                    setScore(scoreRef.current);
                }
            });

            // ÏïÑÏù¥ÌÖú Îì±Ïû•
            if (itemRef.current) {
                // ÏïÑÏù¥ÌÖú Í∑∏Î¶¨Í∏∞
                ctx.font = "20px Arial";
                ctx.fillText("‚≠ê", itemRef.current.x, itemRef.current.y);

                // ÏïÑÏù¥ÌÖúÍ≥º Î∏åÎ¶≠Í±∏ Ï∂©Îèå Ï≤¥ÌÅ¨
                itemRef.current.x -= itemRef.current.speed;
                const girlRight = girlX + frameWidth * scale;
                const girlBottom = girlYRef.current + frameHeight * scale;
                if (
                    itemRef.current.x < girlRight &&            // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏôºÏ™ΩÏúºÎ°ú Îì§Ïñ¥Ïò§Í≥†
                    itemRef.current.y > girlYRef.current &&     // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏïÑÎûòÏ™ΩÏù¥Î©∞
                    itemRef.current.y < girlBottom              // ÏïÑÏù¥ÌÖúÏù¥ Î∏åÎ¶≠Í±∏Î≥¥Îã§ ÏúÑÏ™Ω
                ) {
                    // Ìä∏Î¶¨ÌîåÏÉ∑ Î∞úÎèô
                    setTripleShot(true);
                    clearTimeout(tripleShotTimerRef.current);
                    tripleShotTimerRef.current = setTimeout(() => setTripleShot(false), 5000);
                    // ÏïÑÏù¥ÌÖú Ï†úÍ±∞
                    itemRef.current = null;
                }
                // ÌôîÎ©¥ Î∞ñÏúºÎ°ú ÎÇòÍ∞ÄÎ©¥ Ï†úÍ±∞
                if (itemRef.current && itemRef.current.x < -20) {
                    itemRef.current = null;
                }
            }

            // Ï†êÏàò & Ìä∏Î¶¨ÌîåÏÉ∑ ÌëúÏãú
            ctx.font = "13px 'Press Start 2P'";
            ctx.fillStyle = "#fbbf24";
            ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);

            if (tripleShot) {
                ctx.fillStyle = "#60a5fa";
                ctx.fillText("üî•Triple Shot!", 150, 30);
            }
            // Î∞òÎ≥µ Ìò∏Ï∂ú
            animationRef.current = requestAnimationFrame(draw);
        };

        animationRef.current = requestAnimationFrame(draw);

        // draw();

        const onTouchStart = (e) => {
            if (gameOver || gameClear) {
                // GA Í≤åÏûÑ Ïû¨ÏãúÏûë Ï∂îÏ†Å mobile
                gtag("event", "BG_game_restart_mo", {
                    event_category: "game",
                    event_label: "brickgirl",
                });

                window.location.reload();
                return;
            }

            const touchY = e.touches[0].clientY - canvasRef.current.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 20), canvasRef.current.height - 20);

            isTouching.current = true;

            // Ïù∏ÌÑ∞Î≤å ÏãúÏûë (Í∏∞Ï°¥ Ïù∏ÌÑ∞Î≤å Ï†ïÎ¶¨)
            if (shootIntervalRef.current) {
                clearInterval(shootIntervalRef.current);
            }
            fireBricksRef.current(); // Ï≤´ Î∞úÏÇ¨
            shootIntervalRef.current = setInterval(() => {
                fireBricksRef.current();
            }, 250);
        };

        const onTouchMove = (e) => {
            const touchY = e.touches[0].clientY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 0), canvas.height - frameHeight * scale);
        };

        const onTouchEnd = () => {
            isTouching.current = false;
            clearInterval(shootIntervalRef.current);
            shootIntervalRef.current = null;
        };

        // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏
        window.addEventListener("keydown", (e) => {
            if (["ArrowUp", "ArrowDown", " "].includes(e.key)) {
                e.preventDefault(); // Ïä§ÌÅ¨Î°§ Î∞©ÏßÄ
            }

            if (e.key === "ArrowUp") keys.current.up = true;
            if (e.key === "ArrowDown") keys.current.down = true;
            if (e.key === " " && !shootCooldownRef.current && !gameOver) {
                fireBricksRef.current();
            }
            if ((e.key === "r" || e.key === "R") && (gameOver || gameClear)) {
                // GA Í≤åÏûÑ Ïû¨ÏãúÏûë Ï∂îÏ†Å pc
                gtag("event", "BG_game_restart_pc", {
                    event_category: "game",
                    event_label: "brickgirl",
                });
                
                window.location.reload();
            }
        });

        window.addEventListener("keyup", (e) => {
            if (e.key === "ArrowUp") keys.current.up = false;
            if (e.key === "ArrowDown") keys.current.down = false;
        });


        // Ïù¥Î≤§Ìä∏ Î∞îÏù∏Îî©
        canvas.addEventListener("touchstart", onTouchStart, { passive: false });
        canvas.addEventListener("touchmove", onTouchMove, { passive: false });
        canvas.addEventListener("touchend", onTouchEnd, { passive: false });

        return () => {
            cancelAnimationFrame(animationRef.current);
            clearInterval(shootIntervalRef.current);
        };
    }, [spriteLoaded, canvasSize, gameOver, gameClear, tripleShot]);


    // Ï†Å Îì±Ïû• Í∞ÑÍ≤©
    useEffect(() => {
        const interval = setInterval(() => {
            if (!gameOver && !gameClear) {
                const emojis = ["üöÄ", "üëΩ", "üõ∏"];
                enemiesRef.current.push({
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 20) + 10,
                    speed: Math.random() * 1.5 + 1.5,
                    emoji: emojis[Math.floor(Math.random() * emojis.length)],
                });
            }
        }, 1500);
        return () => clearInterval(interval);
    }, [canvasSize, gameOver, gameClear]);

    // ÏïÑÏù¥ÌÖú ÏÉùÏÑ±
    useEffect(() => {
        const interval = setInterval(() => {
            if (!gameOver && !gameClear && !itemRef.current) {
                itemRef.current = {
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 30) + 10,
                    speed: 2,
                };
            }
        }, 10000);
        return () => clearInterval(interval);
    }, [canvasSize, gameOver, gameClear]);

    // Î∞òÏùëÌòï Ï∫îÎ≤ÑÏä§
    useEffect(() => {
        const updateSize = () => {
            const width = Math.min(window.innerWidth - 10, 480);
            const height = Math.round(width * 0.75);
            setCanvasSize({ width, height });
        };
        updateSize();
        window.addEventListener("resize", updateSize);
        return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
        <div className="flex flex-col items-center mt-6">
            <h2 className="font-press font-bold mb-4 text-rose-400">üß±Brick Girl: Space Defenseüå†</h2>
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                className="bg-black rounded shadow-md touch-none"
                style={{ width: "100%", maxWidth: "480px" }}
            />
            <p className="text-xs text-center mt-4 text-gray-500 dark:text-gray-400 font-press">
                ‚¨Ü UP ‚¨á DOWN ‚Ä¢ SPACE BAR üß±
            </p>
        </div>
    );
}
