import { useEffect, useRef, useState } from "react";

export default function Game() {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

    // Î∏åÎ¶≠Í±∏ ÏúÑÏπò Î∞è Ïù¥Îèô Í¥ÄÎ†®
    const girlX = 30;
    const girlYRef = useRef(150);
    const girlSpeed = 4;
    const keys = useRef({ up: false, down: false });

    // Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïï†ÎãàÎ©îÏù¥ÏÖò Í¥ÄÎ†® ÏÑ§Ï†ï
    const spriteRef = useRef(new Image());
    const frameWidth = 335;   // Math.floor(1024 / 3) = 341
    const frameHeight = 512;  // Math.floor(1024 / 2) = 512
    const numCols = 3;  // Ïù¥ÎØ∏ÏßÄ Í∞ÄÎ°ú ÌîÑÎ†àÏûÑ Ïàò
    const numRows = 2;  // Ïù¥ÎØ∏ÏßÄ ÏÑ∏Î°ú ÌîÑÎ†àÏûÑ Ïàò
    const frameX = useRef(0);
    const frameY = useRef(0);
    const frameCount = useRef(0);
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // Ï†Å Ïö∞Ï£ºÏÑ† ÏÉÅÌÉú
    const enemiesRef = useRef([]);
    const enemySpawnInterval = useRef(null);

    // Î≤ΩÎèå Í¥ÄÎ†®
    const bricksRef = useRef([]);
    const shootCooldownRef = useRef(false);
    const shootIntervalRef = useRef(null);

    // Ìä∏Î¶¨ÌîåÏÉ∑ ÏÉÅÌÉú
    const [tripleShot, setTripleShot] = useState(false);
    const tripleShotTimerRef = useRef(null);

    // ÏïÑÏù¥ÌÖú ÏÉÅÌÉú
    const itemRef = useRef(null);
    const itemSpawnInterval = useRef(null);

    // Ïï†ÎãàÎ©îÏù¥ÏÖò Î£®ÌîÑ
    const animationRef = useRef();

    // Î≥ÑÎπõ Î∞∞Í≤Ω
    const starsRef = useRef([]);

    // Ï†êÏàò ÏÉÅÌÉú
    const [score, setScore] = useState(0);
    const scoreRef = useRef(0);

    // Í≤åÏûÑ ÏÉÅÌÉú
    const [gameOver, setGameOver] = useState(false);
    const [gameClear, setGameClear] = useState(false);

    // Î≥ÑÎπõ Ï¥àÍ∏∞Ìôî
    useEffect(() => {
        const stars = Array.from({ length: 100 }, () => ({
            x: Math.random() * canvasSize.width,
            y: Math.random() * canvasSize.height,
            size: Math.random() * 0.8 + 0.2,
            speed: Math.random() * 1 + 0.5,
        }));
        starsRef.current = stars;
    }, [canvasSize]);

    // Î∏åÎ¶≠Í±∏ Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïù¥ÎØ∏ÏßÄ Î°úÎìú
    useEffect(() => {
        const sprite = spriteRef.current;
        sprite.src = process.env.PUBLIC_URL + "/brickgirl_sprite.png";  // public Ìè¥Îçî Í≤ΩÎ°ú Í∏∞Ï§Ä
        sprite.onload = () => {
            setSpriteLoaded(true);  // Ïù¥ÎØ∏ÏßÄ Î°úÎìú ÏôÑÎ£å
        };
    }, []);

    // Í≤åÏûÑ Î£®ÌîÑ Ïã§Ìñâ
    useEffect(() => {
        if (!spriteLoaded) return;  // Ïù¥ÎØ∏ÏßÄÍ∞Ä ÏïÑÏßÅ Î°úÎìúÎêòÏßÄ ÏïäÏúºÎ©¥ Ïã§Ìñâ X

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Î∞∞Í≤Ω Î≥ÑÎπõ
            ctx.fillStyle = "black";
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "white";
            starsRef.current.forEach((star) => {
                star.x -= star.speed;
                if (star.x < 0) star.x = canvas.width;
                ctx.beginPath();
                ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
                ctx.fill();
            });

            // Í≤åÏûÑ Ï¢ÖÎ£å ÌôîÎ©¥
            if (gameOver || gameClear) {
                const centerX = canvas.width / 2;
                const centerY = canvas.height / 2;
                ctx.textAlign = "center";
                ctx.font = "24px Arial";
                ctx.fillStyle = "white";
                ctx.fillText(gameOver ? "üò• GAME OVER" : "üéâ MISSION COMPLETE!", centerX, centerY - 20);
                ctx.font = "18px Arial";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 10);
                ctx.fillText("üîÑ", centerX, centerY + 40);
                return; // draw Ï§ëÎã®
            }

            // Î∏åÎ¶≠Í±∏ Ïä§ÌîÑÎùºÏù¥Ìä∏ Í∑∏Î¶¨Í∏∞
            // ctx.font = "20px Arial";
            // ctx.fillText("üß±", girlX, girlYRef.current);

            const scale = 0.1;  // Î∏åÎ¶≠Í±∏ ÏÇ¨Ïù¥Ï¶à
            const drawWidth = frameWidth * scale;    // 102.3
            const drawHeight = frameHeight * scale;  // 153.6

            ctx.drawImage(
                spriteRef.current,
                frameX.current * frameWidth,
                frameY.current * frameHeight,
                frameWidth,
                frameHeight,
                girlX,
                girlYRef.current,
                drawWidth,
                drawHeight
            );

            // ÏïÑÏù¥ÌÖú Í∑∏Î¶¨Í∏∞
            if (itemRef.current) {
                ctx.font = "20px Arial";
                ctx.fillText("‚≠ê", itemRef.current.x, itemRef.current.y);
            }

            // ÌîÑÎ†àÏûÑ Ï†ÑÌôò Ïï†ÎãàÎ©îÏù¥ÏÖò
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
                if (frameX.current === 0) frameY.current = (frameY.current + 1) % numRows;
            }
            frameCount.current++;

            // Î∏åÎ¶≠Í±∏ Ïù¥Îèô
            if (keys.current.up && girlYRef.current > -5) girlYRef.current -= girlSpeed;    // 20 Ïà´ÏûêÎ•º Ï§ÑÏù¥Î©¥ ÏúÑÎ°ú Îçî Ïù¥Îèô Í∞ÄÎä•(10)
            if (keys.current.down && girlYRef.current < canvas.height - 50) girlYRef.current += girlSpeed;  // -20 Ïà´ÏûêÎ•º Ïò¨Î¶¨Î©¥ ÏúÑÎ°ú Ïù¥Îèô(-50)

            // Î≤ΩÎèå Ïù¥Îèô Î∞è Ï†Å Ï∂©Îèå Ï≤¥ÌÅ¨
            ctx.font = "16px Arial";
            bricksRef.current.forEach((brick, i) => {
                // brick.x += brick.speed;
                brick.x += brick.speedX;
                brick.y += brick.speedY;
                ctx.fillText("üß±", brick.x, brick.y);

                // Ï†ÅÍ≥º Ï∂©Îèå ÌôïÏù∏
                enemiesRef.current.forEach((enemy, j) => {
                    const dx = brick.x - enemy.x;
                    const dy = brick.y - enemy.y;
                    const distance = Math.sqrt(dx * dx + dy * dy);
                    if (distance < 20) {
                        enemiesRef.current.splice(j, 1);
                        bricksRef.current.splice(i, 1);
                        scoreRef.current += 1;
                        setScore(scoreRef.current);
                        if (scoreRef.current >= 50) setGameClear(true);
                    }
                });

                // enemiesRef.current.forEach((enemy, j) => {
                //     // ÌÉÄÍ≤© ÌåêÏ†ï Î≤îÏúÑ(Box)
                //     const hitbox = 20; // ‚Üê ÏõêÌïòÎäî ÎßåÌÅº Ï°∞Ï†à Í∞ÄÎä• (Í∏∞Î≥∏: 10)

                //     if (
                //         brick.x > enemy.x - hitbox &&
                //         brick.x < enemy.x + hitbox &&
                //         brick.y > enemy.y - hitbox &&
                //         brick.y < enemy.y + hitbox
                //     ) {
                //         bricksRef.current.splice(i, 1);
                //         enemiesRef.current.splice(j, 1);
                //         scoreRef.current++;
                //         setScore(scoreRef.current);
                //         if (scoreRef.current >= 50) setGameClear(true);
                //     }
                // });

                // if (brick.x > canvas.width) bricksRef.current.splice(i, 1);
            });

            // Ï†Å Ïù¥Îèô Î∞è Ï∂©Îèå
            ctx.font = "20px Arial";
            ctx.fillStyle = "#f472b6";
            enemiesRef.current.forEach((enemy, i) => {
                enemy.x -= enemy.speed;
                ctx.fillText(enemy.emoji, enemy.x, enemy.y);

                if (
                    enemy.x < girlX + 10 &&
                    enemy.x > girlX - 10 &&
                    enemy.y > girlYRef.current - 10 &&
                    enemy.y < girlYRef.current + 10
                ) setGameOver(true);

                if (enemy.x < -30) {
                    enemiesRef.current.splice(i, 1);
                    scoreRef.current = Math.max(0, scoreRef.current - 1);
                    setScore(scoreRef.current);
                }
            });

            // ÏïÑÏù¥ÌÖú Îì±Ïû•
            if (itemRef.current) {
                ctx.font = "18px Arial";
                ctx.fillText("üíé", itemRef.current.x, itemRef.current.y);
                itemRef.current.x -= itemRef.current.speed;

                if (
                    itemRef.current.x < girlX + frameWidth &&
                    itemRef.current.y > girlYRef.current &&
                    itemRef.current.y < girlYRef.current + frameHeight
                ) {
                    setTripleShot(true);
                    clearTimeout(tripleShotTimerRef.current);
                    tripleShotTimerRef.current = setTimeout(() => setTripleShot(false), 5000);
                    itemRef.current = null;
                }

                if (itemRef.current?.x < -20) itemRef.current = null;
            }
            // ÏïÑÏù¥ÌÖú Ï∂©Îèå Ï≤¥ÌÅ¨
            // if (
            //     itemRef.current.active &&
            //     girlX < itemRef.current.x + 20 &&
            //     girlX + 30 > itemRef.current.x &&
            //     girlYRef.current < itemRef.current.y + 20 &&
            //     girlYRef.current + 30 > itemRef.current.y
            // ) {
            //     itemRef.current.active = false;
            //     setTripleShot(true);

            //     // 5Ï¥à ÌõÑ Ìö®Í≥º Ï¢ÖÎ£å
            //     clearTimeout(tripleTimerRef.current);
            //     tripleTimerRef.current = setTimeout(() => {
            //         setTripleShot(false);
            //     }, 5000);
            // }

            // Ï†êÏàò & Ìä∏Î¶¨ÌîåÏÉ∑ ÌëúÏãú
            ctx.fillStyle = "#fbbf24";
            ctx.font = "16px Arial";
            ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);
            if (tripleShot) {
                ctx.fillStyle = "#60a5fa";
                ctx.fillText("üî• Triple Shot!", 10, 70);
            }


            // Î∞òÎ≥µ Ìò∏Ï∂ú
            animationRef.current = requestAnimationFrame(draw);
        };

        draw();

        // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏
        const handleKeyDown = (e) => {
            if (e.key === "ArrowUp") keys.current.up = true;
            if (e.key === "ArrowDown") keys.current.down = true;
            if (e.key === " " && !shootCooldownRef.current && !gameOver) {
                fireBricks();
            }
            if ((e.key === "r" || e.key === "R") && (gameOver || gameClear)) window.location.reload();
        };

        // Î≤ΩÎèå Î∞úÏÇ¨ ÏúÑÏπò Ï°∞Ï†ï
        const fireBricks = () => {
            const baseY = girlYRef.current;
            if (tripleShot) {   // Ìä∏Î¶¨ÌîåÏÉ∑
                bricksRef.current.push({ x: girlX + 10, y: baseY + 50, speedX: 6, speedY: 0 });
                bricksRef.current.push({ x: girlX + 10, y: baseY + 30, speedX: 6, speedY: 0 });
                bricksRef.current.push({ x: girlX + 10, y: baseY + 10, speedX: 6, speedY: 0 });
            } else {    // Í∏∞Î≥∏ÏÉ∑
                bricksRef.current.push({ x: girlX + 10, y: baseY + 30, speedX: 6, speedY: 0 });
            }
            shootCooldownRef.current = true;
            setTimeout(() => (shootCooldownRef.current = false), 300);
        };

        const handleKeyUp = (e) => {
            if (e.key === "ArrowUp") keys.current.up = false;
            if (e.key === "ArrowDown") keys.current.down = false;
        };

        // Î™®Î∞îÏùº ÌÑ∞Ïπò Ï†úÏñ¥
        let touchStartY = 0;

        const onTouchStart = (e) => {
            touchStartY = e.touches[0].clientY;

            if (gameOver || gameClear) {
                window.location.reload();
                return;
            }

            const touchY = touchStartY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 20), canvas.height - 20);

            if (!shootIntervalRef.current) {
                shootIntervalRef.current = setInterval(() => {
                    // bricksRef.current.push({ x: girlX + 10, y: girlYRef.current, speed: 6 });
                    fireBricks();
                }, 250);
            }
        };

        const onTouchMove = (e) => {
            const touchY = e.touches[0].clientY - canvas.getBoundingClientRect().top;
            girlYRef.current = Math.min(Math.max(touchY, 20), canvas.height - 20);
        };

        const onTouchEnd = () => {
            clearInterval(shootIntervalRef.current);
            shootIntervalRef.current = null;
        };

        // Ïù¥Î≤§Ìä∏ Î∞îÏù∏Îî©
        window.addEventListener("keydown", handleKeyDown);
        window.addEventListener("keyup", handleKeyUp);
        canvas.addEventListener("touchstart", onTouchStart, { passive: false });
        canvas.addEventListener("touchmove", onTouchMove, { passive: false });
        canvas.addEventListener("touchend", onTouchEnd, { passive: false });

        return () => {
            window.removeEventListener("keydown", handleKeyDown);
            window.removeEventListener("keyup", handleKeyUp);
            canvas.removeEventListener("touchstart", onTouchStart);
            canvas.removeEventListener("touchmove", onTouchMove);
            canvas.removeEventListener("touchend", onTouchEnd);
            cancelAnimationFrame(animationRef.current);
            clearInterval(shootIntervalRef.current);
        };
    }, [spriteLoaded, canvasSize, gameOver, gameClear, tripleShot]);

    // Ï†Å Îì±Ïû• Í∞ÑÍ≤©
    useEffect(() => {
        enemySpawnInterval.current = setInterval(() => {
            if (!gameOver && !gameClear) {
                const emojis = ["üöÄ", "üëΩ", "üõ∏"];
                const randomEmoji = emojis[Math.floor(Math.random() * emojis.length)];
                enemiesRef.current.push({
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 20) + 10,
                    speed: Math.random() * 1.5 + 1.5,
                    emoji: randomEmoji,
                });
            }
        }, 1500);
        return () => clearInterval(enemySpawnInterval.current);
    }, [canvasSize, gameOver, gameClear]);

    // ÏïÑÏù¥ÌÖú ÏÉùÏÑ±
    useEffect(() => {
        itemSpawnInterval.current = setInterval(() => {
            if (!gameOver && !gameClear && !itemRef.current) {
                itemRef.current = {
                    x: canvasSize.width + 20,
                    y: Math.random() * (canvasSize.height - 30) + 10,
                    speed: 2,
                };
            }
        }, 10000);
        return () => clearInterval(itemSpawnInterval.current);
    }, [canvasSize, gameOver, gameClear]);

    // Î∞òÏùëÌòï Ï∫îÎ≤ÑÏä§
    useEffect(() => {
        const updateSize = () => {
            const width = Math.min(window.innerWidth - 10, 480);
            const height = Math.round(width * 0.75);
            setCanvasSize({ width, height });
        };
        updateSize();
        window.addEventListener("resize", updateSize);
        return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
        <div className="flex flex-col items-center mt-6">
            <h2 className="text-2xl font-bold mb-4">üß± Brick Girl: Space Defense</h2>
            <canvas
                ref={canvasRef}
                width={canvasSize.width}
                height={canvasSize.height}
                className="bg-black rounded shadow-md touch-none"
                style={{ width: "100%", maxWidth: "480px" }}
            />
        </div>
    );
}
