import { useEffect, useRef, useState } from "react";

export default function Game() {
    const canvasRef = useRef(null);
    const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

    // 브릭걸 위치 및 이동 관련
    const girlX = 30;
    const girlYRef = useRef(150);
    const girlSpeed = 4;
    const keys = useRef({ up: false, down: false });

    // 스프라이트 애니메이션 관련 설정
    const spriteRef = useRef(new Image());
    const frameWidth = 335;   // Math.floor(1024 / 3) = 341
    const frameHeight = 512;  // Math.floor(1024 / 2) = 512
    const numCols = 3;  // 이미지 가로 프레임 수
    const numRows = 2;  // 이미지 세로 프레임 수
    const frameX = useRef(0);
    const frameY = useRef(0);
    const frameCount = useRef(0);
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // 적 우주선 상태
    const enemiesRef = useRef([]);
    const enemySpawnInterval = useRef(null);

    // 벽돌 관련
    const bricksRef = useRef([]);
    const shootCooldownRef = useRef(false);
    const shootIntervalRef = useRef(null);

    // 트리플샷 상태
    const [tripleShot, setTripleShot] = useState(false);
    const tripleShotTimerRef = useRef(null);

    // 아이템 상태
    const itemRef = useRef(null);
    const itemSpawnInterval = useRef(null);

    // 애니메이션 루프
    const animationRef = useRef();

    // 별빛 배경
    const starsRef = useRef([]);

    // 점수 상태
    const [score, setScore] = useState(0);
    const scoreRef = useRef(0);

    // 게임 상태
    const [gameOver, setGameOver] = useState(false);
    const [gameClear, setGameClear] = useState(false);

    // 별빛 초기화
    useEffect(() => {
        const stars = Array.from({ length: 100 }, () => ({
            x: Math.random() * canvasSize.width,
            y: Math.random() * canvasSize.height,
            size: Math.random() * 0.8 + 0.2,
            speed: Math.random() * 1 + 0.5,
        }));
        starsRef.current = stars;
    }, [canvasSize]);

    // 브릭걸 스프라이트 이미지 로드
    useEffect(() => {
        const sprite = spriteRef.current;
        sprite.src = process.env.PUBLIC_URL + "/brickgirl_sprite.png";  // public 폴더 경로 기준
        sprite.onload = () => {
            setSpriteLoaded(true);  // 이미지 로드 완료
        };
    }, []);

    // 게임 루프 실행
    useEffect(() => {
        if (!spriteLoaded) return;  // 이미지가 아직 로드되지 않으면 실행 X

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // 배경 별빛
            ctx.fillStyle = "black";
            ctx.fillRect(0, 0, canvas.width, canvas.height);
            ctx.fillStyle = "white";
            starsRef.current.forEach((star) => {
                star.x -= star.speed;
                if (star.x < 0) star.x = canvas.width;
                ctx.beginPath();
                ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
                ctx.fill();
            });

            // 게임 종료 화면
            if (gameOver || gameClear) {
                const centerX = canvas.width / 2;
                const centerY = canvas.height / 2;
                ctx.textAlign = "center";
                ctx.font = "24px Arial";
                ctx.fillStyle = "white";
                ctx.fillText(gameOver ? "😥 GAME OVER" : "🎉 MISSION COMPLETE!", centerX, centerY - 20);
                ctx.font = "18px Arial";
                ctx.fillStyle = "#93c5fd";
                ctx.fillText("Tap or Press R to Restart", centerX, centerY + 10);
                ctx.fillText("🔄", centerX, centerY + 40);
                return; // draw 중단
