import React, { useRef, useEffect, useState } from 'react';

const BrickGirlRT = () => {
    const canvasRef = useRef(null);
    const spriteRef = useRef(new Image());

    // Ïä§ÌîÑÎùºÏù¥Ìä∏ ÏÑ§Ï†ï
    const frameWidth = 335;
    const frameHeight = 512;
    const numCols = 3;
    const scale = 0.2;

    const frameX = useRef(0);
    const frameCount = useRef(0);
    const [spriteLoaded, setSpriteLoaded] = useState(false);

    // Î∏åÎ¶≠Í±∏ ÏúÑÏπò Î∞è Ï†êÌîÑ Í¥ÄÎ†® Î≥ÄÏàò
    let girlX = 50;
    let girlY = 0;
    let velocityY = 0;  // velocityYÍ∞Ä ÎÑàÎ¨¥ ÏûëÏúºÎ©¥ ‚Üí Ï∂©Î∂ÑÌïú ÎÜíÏù¥ ÎèÑÎã¨ Î™ªÌï® : -12 ‚Üí -13, -14 Ï°∞Ï†ï. Ïó¨Í∏∞ÏÑú ÎßêÍ≥† ÏïÑÎûòÏ™Ω Í∞í
    let jumpCount = 0;
    const maxJumps = 2;
    const gravity = 0.6;

    const bricks = useRef([]); // Î≤ΩÎèå Î∞∞Ïó¥

    const scrollSpeed = 2;
    const tileSize = 50;
    const groundTiles = useRef([]);

    const levelData = [
        { x: 0, y: 350 }, { x: 50, y: 350 }, { x: 100, y: 350 },
        { x: 150, y: 350 }, { x: 200, y: 350 },
        // Í∞ÑÍ≤© (Ï†êÌîÑ Ïú†ÎèÑ)
        { x: 250, y: 350 }, { x: 350, y: 350 },
        { x: 400, y: 350 },
        // ÎÜíÏùÄ ÏßÄÌòï
        { x: 500, y: 300 }, { x: 550, y: 300 },
        { x: 600, y: 250 }, { x: 650, y: 250 },
        { x: 700, y: 200 }, { x: 750, y: 200 },
    ];

    // Ï¥àÍ∏∞ ÌÉÄÏùº Î°úÎî©
    useEffect(() => {
        groundTiles.current = levelData.map(tile => ({ ...tile, type: 'ground' }));
    }, []);

    // Ï∞©ÏßÄ Ïó¨Î∂Ä Ï≤¥ÌÅ¨ Ìï®Ïàò
    const isOnGround = () => {
        const feetX = girlX + (frameWidth * scale) / 2;
        const feetY = girlY + frameHeight * scale;

        return groundTiles.current.some(tile =>
            feetX > tile.x &&
            feetX < tile.x + tileSize &&
            feetY >= tile.y &&
            feetY <= tile.y + 10
        );
    };

    // Î≤ΩÎèå ÎçòÏßÄÍ∏∞ Ìï®Ïàò
    const throwBrick = () => {
        const startX = girlX + 30;
        const startY = girlY + 10;
        const vx = 6 + Math.random();
        const vy = -8;
        bricks.current.push({ x: startX, y: startY, vx, vy });
    };

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let animationFrameId;

        canvas.width = 800;
        canvas.height = 400;

        // Ïä§ÌîÑÎùºÏù¥Ìä∏ Ïù¥ÎØ∏ÏßÄ Î°úÎî©
        spriteRef.current.src = process.env.PUBLIC_URL + '/brickgirl_sprite.png';
        spriteRef.current.onload = () => {
            setSpriteLoaded(true);
            const standingTile = groundTiles.current.find(tile =>
                girlX + (frameWidth * scale) / 2 > tile.x &&
                girlX + (frameWidth * scale) / 2 < tile.x + tileSize
            );
            if (standingTile) {
                girlY = standingTile.y - frameHeight * scale;
            }
        };

        // ÌÇ§ ÏûÖÎ†• Ï≤òÎ¶¨
        const handleKeyDown = (e) => {
            if (e.code === 'Space') {
                e.preventDefault();
                throwBrick();
            } else if (e.code === 'ArrowUp') {
                if (jumpCount < maxJumps) {
                    velocityY = -12 * (jumpCount === 1 ? 1.2 : 1);
                    // üí° Ï†êÌîÑ Ïãú Î∏åÎ¶≠Í±∏ ÏÇ¥Ïßù ÏïûÏúºÎ°ú ÏõÄÏßÅÏûÑ
                    girlX += 8; // or 10 Ï†ïÎèÑ. Î∏åÎ¶≠Í±∏Ïù¥ Ï†êÌîÑÌïòÎ©∞ ÏïûÏúºÎ°ú ÎÇòÏïÑÍ∞ÄÎäî ÎäêÎÇå
                    jumpCount++;
                }
            }
        };

        // Î™®Î∞îÏùº ÌÑ∞Ïπò Ï°∞Ïûë Ï≤òÎ¶¨
        const handleTouchStart = (e) => {
            const touchX = e.touches[0].clientX;
            if (touchX < canvas.width / 2) {
                if (jumpCount < maxJumps) {
                    velocityY = -12;
                    jumpCount++;
                }
            } else {
                throwBrick();
            }
        };

        const updateFrame = () => {
            frameCount.current++;
            if (frameCount.current % 10 === 0) {
                frameX.current = (frameX.current + 1) % numCols;
            }
        };

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // ÏßÄÌòï Ïä§ÌÅ¨Î°§ Î∞è Í∑∏Î¶¨Í∏∞
            groundTiles.current.forEach(tile => {
                tile.x -= scrollSpeed;
                ctx.fillStyle = 'saddlebrown';
                ctx.fillRect(tile.x, tile.y, tileSize, tileSize);
            });

            // Î¨¥Ìïú ÏßÄÌòï
            const lastTile = groundTiles.current[groundTiles.current.length - 1];
            if (lastTile && lastTile.x < canvas.width) {
                groundTiles.current.push({
                    x: lastTile.x + tileSize,
                    y: 350, // ÏûÑÏãú Í∏∞Î≥∏ ÎÜíÏù¥
                    type: 'ground',
                });
            }

            // Ï§ëÎ†• Ï†ÅÏö© Î∞è Ï†êÌîÑ Ï≤òÎ¶¨
            velocityY += gravity;
            girlY += velocityY;

            // Ï∞©ÏßÄ Ï≤òÎ¶¨
            if (isOnGround()) {
                velocityY = 0;
                jumpCount = 0;
                // Ï∞©ÏßÄÌï† Î∞îÎã• ÌÉÄÏùº Í∏∞Ï§ÄÏúºÎ°ú Ï†ïÌôïÌûà girlY ÏúÑÏπò Ï†ïÎ†¨
                const landingTile = groundTiles.current.find(tile =>
                    girlX + (frameWidth * scale) / 2 > tile.x &&
                    girlX + (frameWidth * scale) / 2 < tile.x + tileSize
                );
                if (landingTile) {
                    const targetY = landingTile.y - frameHeight * scale;
                    // Î∂ÄÎìúÎüΩÍ≤å Î∂ôÎèÑÎ°ù Î≥¥Ï†ï (ÎØ∏ÏÑ∏Ìïú Ïä§ÎÉÖ Ï≤òÎ¶¨)
                    if (girlY > targetY) {
                        girlY = Math.max(girlY - 4, targetY); // Ï†êÌîÑ ÎÇ¥Î†§Ïò¨ Îïå Îπ†Î•¥Í≤å Ï†ïÎ†¨
                    } else {
                        girlY = targetY; // Í±∞Ïùò ÎèÑÏ∞©ÌñàÏúºÎ©¥ Í∑∏ÎÉ• Ïä§ÎÉÖ
                    }
                }
            }

            // Î≤ΩÎèå Ï≤òÎ¶¨
            ctx.font = '16px Arial';
            bricks.current.forEach((brick, index) => {
                brick.x += brick.vx;
                brick.vy += gravity * 0.5;
                brick.y += brick.vy;
                ctx.fillText('üß±', brick.x, brick.y);
                if (brick.x > canvas.width || brick.y > canvas.height) {
                    bricks.current.splice(index, 1);
                }
            });

            // Ïä§ÌîÑÎùºÏù¥Ìä∏ Í∑∏Î¶¨Í∏∞
            if (spriteLoaded) {
                updateFrame();
                ctx.drawImage(
                    spriteRef.current,
                    frameX.current * frameWidth,
                    0,
                    frameWidth,
                    frameHeight,
                    girlX,
                    girlY,
                    frameWidth * scale,
                    frameHeight * scale
                );
            } else {
                ctx.fillStyle = 'hotpink';
                ctx.fillRect(girlX, girlY, 30, 30);
            }

            animationFrameId = requestAnimationFrame(draw);
        };

        // Ïù¥Î≤§Ìä∏ Î¶¨Ïä§ÎÑà Î∞è Î£®ÌîÑ ÏãúÏûë
        window.addEventListener('keydown', handleKeyDown);
        canvas.addEventListener('touchstart', handleTouchStart);
        draw();

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            canvas.removeEventListener('touchstart', handleTouchStart);
            cancelAnimationFrame(animationFrameId);
        };
    }, [spriteLoaded]);

    return (
        <div style={{ textAlign: 'center' }}>
            <h2>Brick Girl: Run & Throw</h2>
            <canvas ref={canvasRef} style={{ border: '2px solid #999' }} />
            <p>‚¨ÜÔ∏è: Jump / Space: üß± (Î™®Î∞îÏùºÏùÄ ÌôîÎ©¥ Ï¢åÏö∞ ÌÑ∞Ïπò)</p>
        </div>
    );
};

export default BrickGirlRT;
