import { useEffect, useRef, useState } from "react";

export default function Game() {
  const canvasRef = useRef(null);
  const [canvasSize, setCanvasSize] = useState({ width: 400, height: 300 });

  // 브릭걸 상태
  const girlX = 50;
  const girlYRef = useRef(0); // 점프 위치
  const isJumping = useRef(false);
  const velocity = useRef(0);

  // 선인장 상태
  const cactusXRef = useRef(canvasSize.width);
  const cactusY = canvasSize.height - 30;
  const cactusPassed = useRef(false);

  // 게임 루프와 속도
  const animationRef = useRef();
  const speedRef = useRef(5); // 기본 선인장 속도

  // 점수 및 상태
  const [score, setScore] = useState(0);
  const scoreRef = useRef(0);
  const [gameOver, setGameOver] = useState(false);
  const [showRestart, setShowRestart] = useState(false);

  // 벽돌 상태
  const bricksRef = useRef([]);

  // 배경 스크롤
  const backgroundXRef = useRef(0);

  // 브릭 발사
  const shootBrick = () => {
    bricksRef.current.push({
      x: girlX + 10,
      y: girlYRef.current,
      speed: 7,
    });
  };

  // 점프 또는 브릭 발사 액션
  const handleAction = (e) => {
    if (e.type === "keydown") {
      if (e.key === " " || e.key === "Spacebar") shootBrick();
      else if (!isJumping.current) {
        isJumping.current = true;
        velocity.current = -15;
      }
    } else {
      shootBrick();
    }
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    girlYRef.current = canvasSize.height - 40;

    const draw = () => {
      // 화면 초기화
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 배경 스크롤 효과
      backgroundXRef.current -= 1;
      if (backgroundXRef.current <= -canvas.width) backgroundXRef.current = 0;
      ctx.fillStyle = "#1e293b";
      ctx.fillRect(backgroundXRef.current, 0, canvas.width, canvas.height);
      ctx.fillRect(backgroundXRef.current + canvas.width, 0, canvas.width, canvas.height);

      // 브릭걸 그리기
      ctx.font = "24px Arial";
      ctx.fillText("🧱", girlX, girlYRef.current);

      // 선인장 그리기
      ctx.font = "20px Arial";
      ctx.fillText("🌵", cactusXRef.current, cactusY);

      // 점프 처리
      if (isJumping.current) {
        girlYRef.current += velocity.current;
        velocity.current += 1;
        if (girlYRef.current >= canvasSize.height - 40) {
          girlYRef.current = canvasSize.height - 40;
          isJumping.current = false;
          velocity.current = 0;
        }
      }

      // 선인장 이동
      cactusXRef.current -= speedRef.current;

      // 벽돌 이동 및 충돌 처리
      ctx.fillStyle = "#f87171";
      bricksRef.current.forEach((brick, index) => {
        brick.x += brick.speed;
        ctx.font = "16px Arial";
        ctx.fillText("🧱", brick.x, brick.y);

        // 선인장과 충돌했을 경우 제거 + 점수
        if (
          brick.x > cactusXRef.current - 10 &&
          brick.x < cactusXRef.current + 10 &&
          brick.y > cactusY - 20 &&
          brick.y < cactusY + 20
        ) {
          cactusXRef.current = canvas.width + Math.random() * 200;
          bricksRef.current.splice(index, 1);
          cactusPassed.current = false;
          scoreRef.current += 1;
          setScore(scoreRef.current);
          speedRef.current += 0.05; // 속도 증가
        }

        if (brick.x > canvas.width + 20) bricksRef.current.splice(index, 1);
      });

      // Game Over 판정
      if (
        cactusXRef.current < girlX + 20 &&
        cactusXRef.current > girlX - 20 &&
        girlYRef.current >= canvasSize.height - 50
      ) {
        setGameOver(true);
        setShowRestart(true);
        cancelAnimationFrame(animationRef.current);
        ctx.fillStyle = "white";
        ctx.font = "24px Arial";
        ctx.fillText("😥 GAME OVER", canvas.width / 2, canvas.height / 2);
        return;
      }

      // 선인장을 지나쳤을 때 점수 증가
      if (!cactusPassed.current && cactusXRef.current + 20 < girlX) {
        cactusPassed.current = true;
        scoreRef.current += 1;
        setScore(scoreRef.current);
        speedRef.current += 0.2;
      }

      // 선인장 리셋
      if (cactusXRef.current + 20 < 0) {
        cactusXRef.current = canvas.width + Math.random() * 200;
        cactusPassed.current = false;
      }

      // 점수 표시
      ctx.fillStyle = "#fbbf24";
      ctx.font = "16px Arial";
      ctx.fillText(`Score: ${scoreRef.current}`, 10, 30);

      animationRef.current = requestAnimationFrame(draw);
    };

    draw();

    // 이벤트 등록
    window.addEventListener("keydown", handleAction);
    canvas.addEventListener("touchstart", handleAction);

    return () => {
      window.removeEventListener("keydown", handleAction);
      canvas.removeEventListener("touchstart", handleAction);
      cancelAnimationFrame(animationRef.current);
    };
  }, [canvasSize]);

  // 반응형 처리
  useEffect(() => {
    const updateSize = () => {
      const width = Math.min(window.innerWidth - 10, 480);
      const height = Math.round(width * 0.75);
      setCanvasSize({ width, height });
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  return (
    <div className="flex flex-col items-center mt-6">
      <h2 className="text-2xl font-bold mb-4">🧱 Brick Girl Run</h2>
      <canvas
        ref={canvasRef}
        width={canvasSize.width}
        height={canvasSize.height}
        className="bg-black rounded shadow-md"
        style={{ width: "100%", maxWidth: "480px" }}
      />

      {/* 게임 오버 시 재시작 버튼 표시 */}
      {showRestart && (
        <button
          onClick={() => window.location.reload()}
          className="mt-4 px-4 py-2 bg-yellow-400 text-black rounded shadow hover:bg-yellow-300 transition"
        >
          🔁 Restart
        </button>
      )}
    </div>
  );
}
