import { useEffect, useRef, useState } from "react";

export default function BrickBreaker() {
    const canvasRef = useRef(null);

    // Í≤åÏûÑ ÏÑ§Ï†ïÍ∞íÎì§
    const canvasWidth = 400;
    const canvasHeight = 300;
    const ballRadius = 10;

    // Ï†êÏàò Î∞è Í≤åÏûÑ ÏÉÅÌÉú
    const [score, setScore] = useState(0);
    const [gameOver, setGameOver] = useState(false);
    const [gameClear, setGameClear] = useState(false);
    const scoreRef = useRef(0);

    // Í≥µ ÏÉÅÌÉú (x, y, ÏÜçÎèÑ)
    const xRef = useRef(canvasWidth / 2);
    const yRef = useRef(canvasHeight - 60);
    const dxRef = useRef(2);
    const dyRef = useRef(-2);

    // Ìå®Îì§ ÏÉÅÌÉú
    const paddleHeight = 10;
    const paddleWidth = 75;
    const paddleXRef = useRef((canvasWidth - paddleWidth) / 2);
    const keys = useRef({ left: false, right: false });
    const animationRef = useRef(null);

    // Î≤ΩÎèå ÏÑ§Ï†ïÍ∞í
    const brickRowCount = 3;
    const brickColumnCount = 6;
    const brickWidth = 50;
    const brickHeight = 20;
    const brickPadding = 10;
    const brickOffsetTop = 30;
    const brickOffsetLeft = 25;

    // Î≤ΩÎèå Î∞∞Ïó¥ Ï¥àÍ∏∞Ìôî
    const bricks = [];
    for (let c = 0; c < brickColumnCount; c++) {
        bricks[c] = [];
        for (let r = 0; r < brickRowCount; r++) {
            bricks[c][r] = { x: 0, y: 0, status: 1 };
        }
    }

    // Í≥µ Í∑∏Î¶¨Í∏∞
    const drawBall = (ctx) => {
        ctx.beginPath();
        ctx.arc(xRef.current, yRef.current, ballRadius, 0, Math.PI * 2);
        ctx.fillStyle = "#ff6347";
        ctx.fill();
        ctx.closePath();
    };

    // Ìå®Îì§ Í∑∏Î¶¨Í∏∞
    const drawPaddle = (ctx) => {
        ctx.beginPath();
        ctx.rect(paddleXRef.current, canvasHeight - paddleHeight, paddleWidth, paddleHeight);
        ctx.fillStyle = "#0095DD";
        ctx.fill();
        ctx.closePath();
    };

    // Î≤ΩÎèå Í∑∏Î¶¨Í∏∞ Î∞è ÎÇ®ÏùÄ Î≤ΩÎèå Ïàò Ï≤¥ÌÅ¨
    const drawBricks = (ctx) => {
        let remainingBricks = 0;
        for (let c = 0; c < brickColumnCount; c++) {
            for (let r = 0; r < brickRowCount; r++) {
                const b = bricks[c][r];
                if (b.status === 1) {
                    remainingBricks++;
                    const brickX = c * (brickWidth + brickPadding) + brickOffsetLeft;
                    const brickY = r * (brickHeight + brickPadding) + brickOffsetTop;
                    b.x = brickX;
                    b.y = brickY;

                    // Í∏∞Î≥∏ Î≤ΩÎèå Ïä§ÌÉÄÏùº(ÏÉâÏÉÅ)
                    //   ctx.beginPath();
                    //   ctx.rect(brickX, brickY, brickWidth, brickHeight);
                    //   ctx.fillStyle = "#fbbf24";
                    //   ctx.fill();
                    //   ctx.closePath();

                    // üß± Ïù¥Î™®ÏßÄ Ïä§ÌÉÄÏùº
                    //   ctx.font = "24px Arial";
                    //   ctx.textAlign = "center";
                    //   ctx.textBaseline = "middle";
                    //   ctx.fillText("üß±", brickX + brickWidth / 2, brickY + brickHeight / 2);


                    // Íµ¨Î©çÎÇú Î≤ΩÎèå
                    // Î≤ΩÎèå Í∏∞Î≥∏ ÏÇ¨Í∞ÅÌòï
                    ctx.beginPath();
                    ctx.fillStyle = "#b91c1c";
                    ctx.fillRect(brickX, brickY, brickWidth, brickHeight);
                    ctx.closePath();

                    // Î≤ΩÎèå Íµ¨Î©ç 3Í∞ú
                    const holeCount = 3;
                    for (let i = 0; i < holeCount; i++) {
                        ctx.beginPath();
                        ctx.arc(
                            brickX + brickWidth * (0.2 + i * 0.3), // 0.2, 0.5, 0.8 ÏúÑÏπò
                            brickY + brickHeight * 0.5,
                            brickHeight * 0.2,
                            0,
                            Math.PI * 2
                        );
                        ctx.fillStyle = "#1f1f1f"; // Íµ¨Î©ç ÏÉâ
                        ctx.fill();
                        ctx.closePath();
                    }
                }
            }
        }
        // Î™®Îì† Î≤ΩÎèå Ï†úÍ±∞ Ïãú ÌÅ¥Î¶¨Ïñ¥ Ï≤òÎ¶¨
        if (remainingBricks === 0 && !gameClear) {
            setGameClear(true);
        }
    };

    // Ï†êÏàò Í∑∏Î¶¨Í∏∞
    // const drawScore = (ctx) => {
    //     ctx.font = "14px Arial";
    //     ctx.fillStyle = "#ffffff";
    //     ctx.fillText(`Score: ${scoreRef.current}`, 8, 20);
    // };

    // Í≤åÏûÑ ÌôîÎ©¥ Ïïà Î©îÏãúÏßÄ Î∞ïÏä§
    const drawEndMessage = (ctx, message) => {
        ctx.font = "24px Arial";
        ctx.fillStyle = "#ffffff";
        ctx.textAlign = "center";
        ctx.fillText(message, canvasWidth / 2, canvasHeight / 2);

        // Restart Î≤ÑÌäº ÌÖçÏä§Ìä∏ ÏïàÎÇ¥
        ctx.font = "14px Arial";
        ctx.fillText("Press R to Restart", canvasWidth / 2, canvasHeight / 2 + 30);
    };

    // Í≥µÍ≥º Î≤ΩÎèå Ï∂©Îèå Í∞êÏßÄ
    const collisionDetection = () => {
        for (let c = 0; c < brickColumnCount; c++) {
            for (let r = 0; r < brickRowCount; r++) {
                const b = bricks[c][r];
                if (b.status === 1) {
                    if (
                        xRef.current > b.x &&
                        xRef.current < b.x + brickWidth &&
                        yRef.current > b.y &&
                        yRef.current < b.y + brickHeight
                    ) {
                        dyRef.current = -dyRef.current;
                        b.status = 0;
                        scoreRef.current += 1;
                        setScore(scoreRef.current);
                    }
                }
            }
        }
    };

    // ÌÇ§Î≥¥Îìú Ïù¥Î≤§Ìä∏ Ìï∏Îì§Îü¨
    const keyDownHandler = (e) => {
        if (e.key === "Right" || e.key === "ArrowRight") keys.current.right = true;
        else if (e.key === "Left" || e.key === "ArrowLeft") keys.current.left = true;
        else if (e.key === "r" || e.key === "R") handleRestart();
    };

    const keyUpHandler = (e) => {
        if (e.key === "Right" || e.key === "ArrowRight") keys.current.right = false;
        else if (e.key === "Left" || e.key === "ArrowLeft") keys.current.left = false;
    };

    // Î¶¨Ïä§ÌÉÄÌä∏ Î≤ÑÌäº ÌÅ¥Î¶≠Ïãú ÏÉàÎ°úÍ≥†Ïπ®
    const handleRestart = () => {
        window.location.reload();
    };

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        document.addEventListener("keydown", keyDownHandler);
        document.addEventListener("keyup", keyUpHandler);

        const draw = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            // drawScore(ctx);
            drawBricks(ctx);
            drawBall(ctx);
            drawPaddle(ctx);
            collisionDetection();

            // Ï¢ÖÎ£å Î©îÏãúÏßÄ
            if (gameOver) {
                drawEndMessage(ctx, "üò¢ GAME OVER");
                cancelAnimationFrame(animationRef.current);
                return;
            }

            if (gameClear) {
                drawEndMessage(ctx, "üéâ CLEAR!!");
                cancelAnimationFrame(animationRef.current);
                return;
            }

            // Î≤Ω Ï∂©Îèå Ï≤òÎ¶¨
            if (
                xRef.current + dxRef.current > canvasWidth - ballRadius ||
                xRef.current + dxRef.current < ballRadius
            ) dxRef.current = -dxRef.current;
            if (yRef.current + dyRef.current < ballRadius) dyRef.current = -dyRef.current;
            // Î∞îÎã• Ï∂©Îèå Ï≤òÎ¶¨ (Í≤åÏûÑ Ïò§Î≤Ñ)
            else if (yRef.current + dyRef.current > canvasHeight - ballRadius) {
                if (
                    xRef.current > paddleXRef.current &&
                    xRef.current < paddleXRef.current + paddleWidth
                ) {
                    dyRef.current = -dyRef.current;
                } else {
                    setGameOver(true);
                    return;
                }
            }

            // ÌÇ§ ÏûÖÎ†•Ïóê Îî∞Î•∏ Ìå®Îì§ Ïù¥Îèô
            if (keys.current.right && paddleXRef.current < canvasWidth - paddleWidth)
                paddleXRef.current += 5;
            else if (keys.current.left && paddleXRef.current > 0)
                paddleXRef.current -= 5;

            // Í≥µ Ïù¥Îèô
            xRef.current += dxRef.current;
            yRef.current += dyRef.current;

            animationRef.current = requestAnimationFrame(draw);
        };

        draw();

        return () => {
            document.removeEventListener("keydown", keyDownHandler);
            document.removeEventListener("keyup", keyUpHandler);
            cancelAnimationFrame(animationRef.current);
        };
    }, [gameOver, gameClear]);

    return (
        <div className="mt-8 flex flex-col items-center">
            <canvas
                ref={canvasRef}
                width={canvasWidth}
                height={canvasHeight}
                className="bg-black rounded shadow-md"
            />
        </div>
    );
}
